INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'50CF912E-E224-4DF5-A91E-E6EF7A7516F8', NULL, NULL, N'CGSB-43.123 4.1.4.2', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'TC-2R aerosol containers shall be equipped with a pressure-relief device that shall be designed to function above the maximum internal pressure allowed in a TC-2R container (1500 kPa) and prior to bursting of the container.', N'Clause', N'CGSB-43.123 4.1.4.2', N'TDGA 5 (c)', N'TDGR 5.11', N'3042', N'Les bombes aérosol TC-2R doivent être dotées d’un dispositif de détente de surpression conçu pour fonctionner au-dessus de la pression interne maximale autorisée pour un contenant TC-2R (1500 kPa) et avant la rupture du contenant.', N'TC-2R aerosol containers shall be equipped with a pressure-relief device that shall be designed to function above the maximum internal pressure allowed in a TC-2R container (1500 kPa) and prior to bursting of the container.', N'Les bombes aérosol TC-2R doivent être dotées d’un dispositif de détente de surpression conçu pour fonctionner au-dessus de la pression interne maximale autorisée pour un contenant TC-2R (1500 kPa) et avant la rupture du contenant.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'D3585C3A-AC88-4F5D-811A-43C219ADE7C1', NULL, NULL, N'CGSB-43.123 4.1.5', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'Openings shall be circular and in the ends of containers.', N'Clause', N'CGSB-43.123 4.1.5', N'TDGA 5 (c)', N'TDGR 5.11', N'3043', N'Les ouvertures doivent être circulaires et situées aux extrémités des contenants.', N'Openings shall be circular and in the ends of containers.', N'Les ouvertures doivent être circulaires et situées aux extrémités des contenants.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'AC1C4145-5F73-48A9-943E-A1279A2973AC', NULL, NULL, N'CGSB-43.123 4.1.6', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'Closures shall be designed and manufactured so that under normal conditions of transport, including handling, the closures remain secure and do not leak.', N'Clause', N'CGSB-43.123 4.1.6', N'TDGA 5 (c)', N'TDGR 5.11', N'3044', N'Les dispositifs de fermeture doivent être conçus et fabriqués de sorte que, dans des conditions normales de transport, y compris la manutention, ils demeurent solidement fermés et ne fuient pas.', N'Closures shall be designed and manufactured so that under normal conditions of transport, including handling, the closures remain secure and do not leak.', N'Les dispositifs de fermeture doivent être conçus et fabriqués de sorte que, dans des conditions normales de transport, y compris la manutention, ils demeurent solidement fermés et ne fuient pas.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'CA23E6F6-3C55-4872-9BC3-61D0DBF05AAD', NULL, NULL, N'CGSB-43.123 4.1.7.1', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'Containers may be seamless or with seams, except that a seam in the cylindrical portion of a TC-2R aerosol container is not authorized. Circumferential seams shall be welded, swedged, brazed, soldered or doubleseamed. Longitudinal seams shall be welded, brazed or soldered.', N'Clause', N'CGSB-43.123 4.1.7.1', N'TDGA 5 (c)', N'TDGR 5.11', N'3045', N'Les contenants peuvent être fabriqués avec ou sans soudures. Par contre, il est interdit de souder la partie cylindrique d’une bombe aérosol TC-2R. Les joints circonférentiels doivent être réalisés par soudage, estampage, brasage fort, brasage tendre ou double oudure. Les joints longitudinaux doivent être réalisés par soudage, brasage fort ou brasage tendre.', N'Containers may be seamless or with seams, except that a seam in the cylindrical portion of a TC-2R aerosol container is not authorized. Circumferential seams shall be welded, swedged, brazed, soldered or doubleseamed. Longitudinal seams shall be welded, brazed or soldered.', N'Les contenants peuvent être fabriqués avec ou sans soudures. Par contre, il est interdit de souder la partie cylindrique d’une bombe aérosol TC-2R. Les joints circonférentiels doivent être réalisés par soudage, estampage, brasage fort, brasage tendre ou double oudure. Les joints longitudinaux doivent être réalisés par soudage, brasage fort ou brasage tendre.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'0A78EDF8-CEA9-418C-A4C8-3C88148B0B93', NULL, NULL, N'CGSB-43.123 4.1.7.2', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'Welded seams shall have a design strength no less than the minimum ultimate tensile strength of the shell material in the completed container.', N'Clause', N'CGSB-43.123 4.1.7.2', N'TDGA 5 (c)', N'TDGR 5.11', N'3046', N'La résistance nominale des joints soudés doit être au moins équivalente à la résistance minimale à la traction du matériau de la paroi du contenant terminé.', N'Welded seams shall have a design strength no less than the minimum ultimate tensile strength of the shell material in the completed container.', N'La résistance nominale des joints soudés doit être au moins équivalente à la résistance minimale à la traction du matériau de la paroi du contenant terminé.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'96226905-2D31-41D9-9D57-2D7AC54F85BD', NULL, NULL, N'CGSB-43.123 4.1.7.3', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'The minimum width of brazed joints shall be at least four times the thickness of the shell wall.', N'Clause', N'CGSB-43.123 4.1.7.3', N'TDGA 5 (c)', N'TDGR 5.11', N'3047', N'La largeur minimale des joints brasés doit être d’au moins quatre fois l’épaisseur de la paroi du contenant.', N'The minimum width of brazed joints shall be at least four times the thickness of the shell wall.', N'La largeur minimale des joints brasés doit être d’au moins quatre fois l’épaisseur de la paroi du contenant.', NULL)
