INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'638CB1E8-79AA-45B5-A359-77552590DCB1', NULL, NULL, N'CGSB-43.123 4.1.7.4', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'The brazing material shall have a melting point of not less than 540°C.', N'Clause', N'CGSB-43.123 4.1.7.4', N'TDGA 5 (c)', N'TDGR 5.11', N'3048', N'Le métal d’apport pour brasage doit avoir un point de fusion d’au moins 540 °C.', N'The brazing material shall have a melting point of not less than 540°C.', N'Le métal d’apport pour brasage doit avoir un point de fusion d’au moins 540 °C.', NULL)
