INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'49A678E0-6AAC-4F33-8E69-0F274CAC4446', NULL, NULL, N'CGSB-43.123 4.1.7.5', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'Brazing on aluminum containers shall be prohibited.', N'Clause', N'CGSB-43.123 4.1.7.5', N'TDGA 5 (c)', N'TDGR 5.11', N'3049', N'Le brasage sur les contenants en aluminium est interdit.', N'Brazing on aluminum containers shall be prohibited.', N'Le brasage sur les contenants en aluminium est interdit.', NULL)
