INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'A28D2408-C06B-473E-95CB-2CAF86B0B8FB', NULL, NULL, N'CGSB-43.123 4.1.7.6', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'The containers shall be manufactured by equipment and methods that ensure the uniformity of the completed containers. The surface finish of the container shall be smooth and uniform with dirt and scale removed. Defects likely to weaken the completed container shall be prohibited.', N'Clause', N'CGSB-43.123 4.1.7.6', N'TDGA 5 (c)', N'TDGR 5.11', N'3050', N'Les contenants doivent être fabriqués en utilisant l’appareillage et les méthodes nécessaires pour assurer l’uniformité des contenants terminés. Le fini de la surface des contenants doit être lisse et uniforme, sans saletés ni calamines. Le contenant terminé ne doit présenter aucun défaut susceptible de diminuer sa résistance.', N'The containers shall be manufactured by equipment and methods that ensure the uniformity of the completed containers. The surface finish of the container shall be smooth and uniform with dirt and scale removed. Defects likely to weaken the completed container shall be prohibited.', N'Les contenants doivent être fabriqués en utilisant l’appareillage et les méthodes nécessaires pour assurer l’uniformité des contenants terminés. Le fini de la surface des contenants doit être lisse et uniforme, sans saletés ni calamines. Le contenant terminé ne doit présenter aucun défaut susceptible de diminuer sa résistance.', NULL)
GO
