INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'867A2643-6A59-430C-9927-AB852DC63CF8', NULL, NULL, N'CGSB-43.123 4.2.1', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'All containers selected for testing shall be traceable to the lot of containers they represent.', N'Clause', N'CGSB-43.123 4.2.1', N'TDGA 5 (c)', N'TDGR 5.11', N'3051', N'Tous les contenants prélevés pour essais doivent être traçables à leur lot d’origine.', N'All containers selected for testing shall be traceable to the lot of containers they represent.', N'Tous les contenants prélevés pour essais doivent être traçables à leur lot d’origine.', NULL)
