INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'C3DBBFE7-37CE-4AEF-A817-93939DC76CAE', NULL, NULL, N'CGSB-43.123 4.2.3.3', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'For TC-2R aerosol containers, the burst test specified in 4.2.3.1 shall be conducted on one completed container, without a pressure-relief device, from each lot of 5000 or fewer containers. The containers used for the internal pressure test specified in 4.2.2 may be used for the burst test.', N'Clause', N'CGSB-43.123 4.2.3.3', N'TDGA 5 (c)', N'TDGR 5.11', N'3056', N'Pour les bombes aérosol TC-2R, l’essai de rupture spécifié en 4.2.3.1 doit être réalisé sur un contenant
terminé, non doté d’un dispositif de détente de surpression, prélevé de chaque lot de 5000 contenants ou moins. Les contenants utilisés pour l’essai de pression interne spécifié en 4.2.2 peuvent également servir pour l’essai de rupture.', N'For TC-2R aerosol containers, the burst test specified in 4.2.3.1 shall be conducted on one completed container, without a pressure-relief device, from each lot of 5000 or fewer containers. The containers used for the internal pressure test specified in 4.2.2 may be used for the burst test.', N'Pour les bombes aérosol TC-2R, l’essai de rupture spécifié en 4.2.3.1 doit être réalisé sur un contenant
terminé, non doté d’un dispositif de détente de surpression, prélevé de chaque lot de 5000 contenants ou moins. Les contenants utilisés pour l’essai de pression interne spécifié en 4.2.2 peuvent également servir pour l’essai de rupture.', NULL)
