INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'1AE54810-D3F7-4CF3-A461-BF4D00367AA5', NULL, NULL, N'CGSB-43.123 7.1.1', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'This section specifies the Transport Canada registration requirements for manufacturers of TC specification aerosol containers and gas cartridges. A person shall not manufacture TC specification aerosols or gas cartridges under this standard unless the manufacturing facility is registered with the Executive Director.', N'Clause', N'CGSB-43.123 7.1.1', N'TDGA 5 (c)', N'TDGR 5.11', N'3108', N'Le présent alinéa spécifie les exigences relatives à l’inscription auprès de Transports Canada pour les fabricants des bombes aérosol et des cartouches à gaz conformes à une spécification de TC. Nul ne peut fabriquer des bombes aérosol ou des cartouches à gaz conforme à une spécification de TC selon la présente norme à moins que le directeur exécutif n’ait approuvé l’inscription de l’installation de fabrication.', N'This section specifies the Transport Canada registration requirements for manufacturers of TC specification aerosol containers and gas cartridges. A person shall not manufacture TC specification aerosols or gas cartridges under this standard unless the manufacturing facility is registered with the Executive Director.', N'Le présent alinéa spécifie les exigences relatives à l’inscription auprès de Transports Canada pour les fabricants des bombes aérosol et des cartouches à gaz conformes à une spécification de TC. Nul ne peut fabriquer des bombes aérosol ou des cartouches à gaz conforme à une spécification de TC selon la présente norme à moins que le directeur exécutif n’ait approuvé l’inscription de l’installation de fabrication.', NULL)
GO
