INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'3E7885EF-397C-4839-9FA1-5EE4D6B8FF35', NULL, NULL, N'CGSB-43.123 7.2.1', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'A manufacturer of TC specification aerosol containers and/or gas cartridges shall obtain a Certificate of
registration from the Executive Director prior to the application of markings in compliance with clause 5 of this standard. The registered manufacturer shall perform its functions at the location stipulated on the Certificate of registration unless the Certificate authorizes the facility to conduct these activities elsewhere.', N'Clause', N'CGSB-43.123 7.2.1', N'TDGA 5 (c)', N'TDGR 5.11', N'3111', N'Le fabricant de bombes aérosol et/ou de cartouches à gaz conforme à une spécification de TC doit obtenir un certificat d’inscription du directeur exécutif avant d’effectuer le marquage, conformément à la section 5 de la présente norme. Le fabricant inscrit doit mener ses activités à l’endroit spécifié sur le certificat d’inscription, sauf s’il est indiqué que les activités peuvent être réalisées ailleurs.', N'A manufacturer of TC specification aerosol containers and/or gas cartridges shall obtain a Certificate of
registration from the Executive Director prior to the application of markings in compliance with clause 5 of this standard. The registered manufacturer shall perform its functions at the location stipulated on the Certificate of registration unless the Certificate authorizes the facility to conduct these activities elsewhere.', N'Le fabricant de bombes aérosol et/ou de cartouches à gaz conforme à une spécification de TC doit obtenir un certificat d’inscription du directeur exécutif avant d’effectuer le marquage, conformément à la section 5 de la présente norme. Le fabricant inscrit doit mener ses activités à l’endroit spécifié sur le certificat d’inscription, sauf s’il est indiqué que les activités peuvent être réalisées ailleurs.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'DA62088D-20CE-420E-BC03-FB42B597648D', NULL, NULL, N'CGSB-43.123 7.4.3 a)', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'A Certificate of registration shall remain valid beyond its expiry date if an application for renewal of registration is made in accordance with 7.4.2 and the Executive Director has not requested information supplementary to the original application for renewal.', N'Clause', N'CGSB-43.123 7.4.3 a)', N'TDGA 5 (c)', N'TDGR 5.11', N'3112', N'Un certificat d’inscription est valide au-delà de sa date d’expiration, si une demande de renouvellement a été faite, conformément aux exigences prescrites en 7.4.2, et que le directeur exécutif n’a pas demandé de renseignements supplémentaires à la demande initiale aux fins de renouvellement.', N'A Certificate of registration shall remain valid beyond its expiry date if an application for renewal of registration is made in accordance with 7.4.2 and the Executive Director has not requested information supplementary to the original application for renewal.', N'Un certificat d’inscription est valide au-delà de sa date d’expiration, si une demande de renouvellement a été faite, conformément aux exigences prescrites en 7.4.2, et que le directeur exécutif n’a pas demandé de renseignements supplémentaires à la demande initiale aux fins de renouvellement.', NULL)
