INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'A34B3296-E2BF-4CCE-985A-203EC8E902D1', NULL, NULL, N'CGSB-43.123 7.5.1', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'A Certificate of registration issued in accordance with the CGSB-43.123-2010 standard shall be deemed to be registration as a manufacturing facility pursuant to clause 7 of this standard unless the Certificate has expired or been revoked.', N'Clause', N'CGSB-43.123 7.5.1', N'TDGA 5 (c)', N'TDGR 5.11', N'3114', N'Un certificat d’inscription délivré selon la norme CGSB-43.123-2010 doit être considéré comme étant une inscription d’une installation de fabrication conformément aux exigences prescrites à la section 7 de la présente norme à moins que le certificat ne soit expiré ou révoqué.', N'A Certificate of registration issued in accordance with the CGSB-43.123-2010 standard shall be deemed to be registration as a manufacturing facility pursuant to clause 7 of this standard unless the Certificate has expired or been revoked.', N'Un certificat d’inscription délivré selon la norme CGSB-43.123-2010 doit être considéré comme étant une inscription d’une installation de fabrication conformément aux exigences prescrites à la section 7 de la présente norme à moins que le certificat ne soit expiré ou révoqué.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'9E67CF65-AEF5-4E94-842B-785F8B82DF26', NULL, NULL, N'CGSB-43.123 8.1.1.1', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'No person shall handle, offer for transport or transport dangerous goods classified as UN1950 or UN2037 unless the internal pressure in the containers at 55°C, when filled for transport with the gas, is within one of the pressure ranges listed in column 1 of Table 1 and the container is in compliance with the condition in column 2 that corresponds to the pressure range. The TC specification containers listed in column 2 of Table 1 shall be manufactured, tested, marked and filled in compliance with the applicable requirements of this standard.', N'Clause', N'CGSB-43.123 8.1.1.1', N'TDGA 5 (c)', N'TDGR 5.11', N'3115', N'Il est interdit de manutentionner, de présenter au transport ou de transporter des marchandises dangereuses classifiées UN1950 ou UN2037 à moins que la pression interne dans les contenants à 55 °C, lorsqu’ils sont remplis de gaz en vue de leur transport, ne soit comprise dans l’une des plages de pressions présentées à la colonne 1 du tableau 1 et que le contenant ne soit conforme à la condition de la colonne 2 correspondant à la plage de pression. Les contenants conformes à une spécification de TC indiqués à la colonne 2 doivent être fabriqués, mis à l’essai, marqués et remplis conformément aux exigences applicables de la présente norme.', N'No person shall handle, offer for transport or transport dangerous goods classified as UN1950 or UN2037 unless the internal pressure in the containers at 55°C, when filled for transport with the gas, is within one of the pressure ranges listed in column 1 of Table 1 and the container is in compliance with the condition in column 2 that corresponds to the pressure range. The TC specification containers listed in column 2 of Table 1 shall be manufactured, tested, marked and filled in compliance with the applicable requirements of this standard.', N'Il est interdit de manutentionner, de présenter au transport ou de transporter des marchandises dangereuses classifiées UN1950 ou UN2037 à moins que la pression interne dans les contenants à 55 °C, lorsqu’ils sont remplis de gaz en vue de leur transport, ne soit comprise dans l’une des plages de pressions présentées à la colonne 1 du tableau 1 et que le contenant ne soit conforme à la condition de la colonne 2 correspondant à la plage de pression. Les contenants conformes à une spécification de TC indiqués à la colonne 2 doivent être fabriqués, mis à l’essai, marqués et remplis conformément aux exigences applicables de la présente norme.', NULL)
GO
