INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'DDC96B98-9CBB-412F-A971-E917A560813C', NULL, NULL, N'CGSB-43.123 8.1.3.1', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'After a person fills a container, and before it is placed in transport, the container shall be subjected to a test in a hot water bath. The temperature of the water bath and the duration of the test shall be such that the internal pressure reaches that which would be reached at 55 °C. If, when subjected to this test, the container leaks, becomes permanently deformed, or displays any other defects, it shall not be used to transport dangerous goods. A TC-2S aerosol container may be deformed through softening provided that it does not leak.', N'Clause', N'CGSB-43.123 8.1.3.1', N'TDGA 5 (c)', N'TDGR 5.11', N'3117', N'Après avoir été rempli et avant d’être transporté, le contenant doit être mis à l’essai par immersion dans un réservoir d’eau chaude. La température de l’eau du réservoir et la durée de l’essai doivent faire en sorte que la pression interne du contenant atteigne ce qu’elle devrait être à 55 °C. Si au cours de cet essai, le contenant fuit, subit une déformation permanente ou présente toute autre défectuosité, il ne doit pas être utilisé pour transporter des marchandises dangereuses. Une bombe aérosol TC-2S peut être déformée par ramollissement pourvu qu’elle ne fuie pas.', N'After a person fills a container, and before it is placed in transport, the container shall be subjected to a test in a hot water bath. The temperature of the water bath and the duration of the test shall be such that the internal pressure reaches that which would be reached at 55 °C. If, when subjected to this test, the container leaks, becomes permanently deformed, or displays any other defects, it shall not be used to transport dangerous goods. A TC-2S aerosol container may be deformed through softening provided that it does not leak.', N'Après avoir été rempli et avant d’être transporté, le contenant doit être mis à l’essai par immersion dans un réservoir d’eau chaude. La température de l’eau du réservoir et la durée de l’essai doivent faire en sorte que la pression interne du contenant atteigne ce qu’elle devrait être à 55 °C. Si au cours de cet essai, le contenant fuit, subit une déformation permanente ou présente toute autre défectuosité, il ne doit pas être utilisé pour transporter des marchandises dangereuses. Une bombe aérosol TC-2S peut être déformée par ramollissement pourvu qu’elle ne fuie pas.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'892E5E5F-D9FD-4A52-9C9B-BDAB8CFD5BBB', NULL, NULL, N'CGSB-43.125 7.3.1.5', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'The test packagings shall be dropped on a rigid, non-resilient, flat, massive and horizontal surface.', N'Clause', N'CGSB-43.125 7.3.1.5', N'TDGA 5 (c)', NULL, N'3205', N'On laisse tomber les emballages d’essai sur une surface horizontale plane, résistante, rigide et massive.', N'The test packagings shall be dropped on a rigid, non-resilient, flat, massive and horizontal surface.', N'On laisse tomber les emballages d’essai sur une surface horizontale plane, résistante, rigide et massive.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'3054F410-E50F-4093-BFCA-B2EDAC5F899C', NULL, NULL, N'CGSB-43.125 7.3.1.6', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'The drop test shall be performed with the packagings in the conditioning atmosphere, specified in 7.3.2, or within 15 min of removal from the conditioning atmosphere.', N'Clause', N'CGSB-43.125 7.3.1.6', N'TDGA 5 (c)', NULL, N'3206', N'L’essai de chute doit être réalisé avec les emballages dans l’atmosphère de conditionnement, spécifiée en 7.3.2, ou dans les 15 min suivant le retrait de l’atmosphère de conditionnement.', N'The drop test shall be performed with the packagings in the conditioning atmosphere, specified in 7.3.2, or within 15 min of removal from the conditioning atmosphere.', N'L’essai de chute doit être réalisé avec les emballages dans l’atmosphère de conditionnement, spécifiée en 7.3.2, ou dans les 15 min suivant le retrait de l’atmosphère de conditionnement.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'17C4110F-9832-4446-8792-8EB527F04C1C', NULL, NULL, N'CGSB-43.123 8.1.3.3', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'If a substance to be contained in a container deteriorates by heat, or if the container is made of plastic material which softens at the test temperature specified in 8.1.3.1 or 8.1.3.2, the temperature referred to in those clauses may be reduced to between 20°C and 30°C. In addition, the test in 8.1.3.1 shall be conducted on a sample from every lot of 2000 or fewer containers at the temperature referred to in 8.1.3.1 or 8.1.3.2. If the sample container leaks, becomes permanently deformed, or displays any other defect, the lot of containers from which the sample was taken shall be condemned. A TC-2S aerosol container may be deformed through softening provided that it does not leak.', N'Clause', N'CGSB-43.123 8.1.3.3', N'TDGA 5 (c)', N'TDGR 5.11', N'3118', N'Si une substance devant être placée dans un contenant se détériore à la chaleur ou si un contenant fait de matière plastique se ramollit à la température d’essai indiquée en 8.1.3.1 ou en 8.1.3.2, la température spécifiée dans ces paragraphes peut être réduite pour se situer entre 20 °C et 30 °C. En outre, l’essai en 8.1.3.1 doit être réalisé à l’aide d’un échantillon prélevé de chaque lot de 2000 contenants ou moins, à la température indiquée en 8.1.3.1 ou en 8.1.3.2. Si l’échantillon fuit, subit une déformation permanente ou présente toute autre défectuosité, le lot de contenants d’où provient l’échantillon doit être refusé. Une bombe aérosol TC-2S peut être déformée par ramollissement pourvu qu’elle ne fuie pas.', N'If a substance to be contained in a container deteriorates by heat, or if the container is made of plastic material which softens at the test temperature specified in 8.1.3.1 or 8.1.3.2, the temperature referred to in those clauses may be reduced to between 20°C and 30°C. In addition, the test in 8.1.3.1 shall be conducted on a sample from every lot of 2000 or fewer containers at the temperature referred to in 8.1.3.1 or 8.1.3.2. If the sample container leaks, becomes permanently deformed, or displays any other defect, the lot of containers from which the sample was taken shall be condemned. A TC-2S aerosol container may be deformed through softening provided that it does not leak.', N'Si une substance devant être placée dans un contenant se détériore à la chaleur ou si un contenant fait de matière plastique se ramollit à la température d’essai indiquée en 8.1.3.1 ou en 8.1.3.2, la température spécifiée dans ces paragraphes peut être réduite pour se situer entre 20 °C et 30 °C. En outre, l’essai en 8.1.3.1 doit être réalisé à l’aide d’un échantillon prélevé de chaque lot de 2000 contenants ou moins, à la température indiquée en 8.1.3.1 ou en 8.1.3.2. Si l’échantillon fuit, subit une déformation permanente ou présente toute autre défectuosité, le lot de contenants d’où provient l’échantillon doit être refusé. Une bombe aérosol TC-2S peut être déformée par ramollissement pourvu qu’elle ne fuie pas.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'5A8D9F83-817D-48FF-8DA0-4A095C168CCB', NULL, NULL, N'CGSB-43.123 8.1.3.4', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'The following records shall be retained for a minimum of ten years: Records of test results for the hot water bath test with a record of both the temperature and duration of the test.', N'Clause', N'CGSB-43.123 8.1.3.4', N'TDGA 5 (c)', N'TDGR 5.11', N'3119', N'Les registres suivants doivent être conservés pendant au moins dix ans : les résultats d’essai dans le bain d’eau chaude avec la température et la durée de l’essai.', N'The following records shall be retained for a minimum of ten years: Records of test results for the hot water bath test with a record of both the temperature and duration of the test.', N'Les registres suivants doivent être conservés pendant au moins dix ans : les résultats d’essai dans le bain d’eau chaude avec la température et la durée de l’essai.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'02D90D06-6F45-4F87-A7F3-EB9AB3E20DFE', NULL, NULL, N'CGSB-43.123 8.1.4.2', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'Prior to filling, each container shall be subjected to an internal pressure equal to or greater than the internal pressure in the containers at 55°C when filled for transport with the gas. This pressure shall be at least two-thirds of the minimum burst pressure of the container. If any container shows evidence of distortion, leakage at a rate equal to or greater than 3.3 x 10-2 mbar ∙L ∙s-1 at 20°C at the test pressure, or any other defect, it shall be condemned.', N'Clause', N'CGSB-43.123 8.1.4.2', N'TDGA 5 (c)', N'TDGR 5.11', N'3120', N'Avant le remplissage, chaque contenant doit être soumis à une pression interne égale ou supérieure à la pression interne des contenants à 55 °C lorsqu’ils sont remplis de gaz en vue de leur transport. Cette pression doit correspondre au moins aux deux tiers de la pression de rupture minimale du contenant. Tout contenant qui présente une distorsion, une fuite à un taux égal ou supérieur à 3,3 x 10-2 mbar ∙L∙s-1 à 20 °C à la pression d’essai, ou toute autre défectuosité, doit être refusé.', N'Prior to filling, each container shall be subjected to an internal pressure equal to or greater than the internal pressure in the containers at 55°C when filled for transport with the gas. This pressure shall be at least two-thirds of the minimum burst pressure of the container. If any container shows evidence of distortion, leakage at a rate equal to or greater than 3.3 x 10-2 mbar ∙L ∙s-1 at 20°C at the test pressure, or any other defect, it shall be condemned.', N'Avant le remplissage, chaque contenant doit être soumis à une pression interne égale ou supérieure à la pression interne des contenants à 55 °C lorsqu’ils sont remplis de gaz en vue de leur transport. Cette pression doit correspondre au moins aux deux tiers de la pression de rupture minimale du contenant. Tout contenant qui présente une distorsion, une fuite à un taux égal ou supérieur à 3,3 x 10-2 mbar ∙L∙s-1 à 20 °C à la pression d’essai, ou toute autre défectuosité, doit être refusé.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'E93D4245-BA12-49A9-9FBB-73A2F55A7860', NULL, NULL, N'CGSB-43.123 8.1.4.3', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'The temperature referred to in 8.1.4.2 may be reduced to 50°C if the container is not more than 95% liquid full at 50°C.', N'Clause', N'CGSB-43.123 8.1.4.3', N'TDGA 5 (c)', N'TDGR 5.11', N'3121', N'La température indiquée en 8.1.4.2 peut être réduite à 50 °C, si au plus 95 % du contenant est rempli de liquide à 50 °C.', N'The temperature referred to in 8.1.4.2 may be reduced to 50°C if the container is not more than 95% liquid full at 50°C.', N'La température indiquée en 8.1.4.2 peut être réduite à 50 °C, si au plus 95 % du contenant est rempli de liquide à 50 °C.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'BA5895ED-EC17-4AAE-BA0D-25F2D61CDC8F', NULL, NULL, N'CGSB-43.123 8.1.4.4', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'Once filled, each container shall be leak-tested. If a container shows evidence of deformation or a leakage rate greater than 2.0 x 10-3 mbar ∙L ∙s-1 at 20°C, it shall be condemned.', N'Clause', N'CGSB-43.123 8.1.4.4', N'TDGA 5 (c)', N'TDGR 5.11', N'3122', N'Une fois rempli, chaque contenant doit être mis à l’essai pour vérifier s’il fuit. Un contenant doit être refusé s’il présente une déformation ou si le taux de fuite est supérieur à 2,0 x 10-3 mbar ∙L ∙s-1 à 20 °C.', N'Once filled, each container shall be leak-tested. If a container shows evidence of deformation or a leakage rate greater than 2.0 x 10-3 mbar ∙L ∙s-1 at 20°C, it shall be condemned.', N'Une fois rempli, chaque contenant doit être mis à l’essai pour vérifier s’il fuit. Un contenant doit être refusé s’il présente une déformation ou si le taux de fuite est supérieur à 2,0 x 10-3 mbar ∙L ∙s-1 à 20 °C.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'CCF39034-5272-4B92-8384-F822BC96700A', NULL, NULL, N'CSA B620-20 5.2.2.8 g) iii) 1)', N'CSA B620 Highway tanks and TC portable tanks for the transportation of dangerous goods', NULL, NULL, N'Clause', N'CSA B620-20 5.2.2.8 g) iii) 1)', NULL, NULL, N'886', NULL, NULL, NULL, NULL)
