INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'A4485FE1-E92D-4768-9159-862A470925D3', NULL, NULL, N'CGSB-43.123 8.1.9.1', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'A container manufactured and tested in compliance with this standard shall not be used to handle, offer for transport or transport dangerous goods if the contents meet the packing group I criteria specified in Part 2, Classification, of the TDG Regulations.', N'Clause', N'CGSB-43.123 8.1.9.1', N'TDGA 5 (c)', N'TDGR 5.11', N'3127', N'Un contenant fabriqué et mis à l’essai conformément aux exigences de la présente norme ne doit pas être utilisé pour la présentation au transport, la manutention ou le transport de marchandises dangereuses si son contenu satisfait aux critères du groupe d’emballage I, prescrits dans la partie 2, Classification, du Règlement sur le TMD.', N'A container manufactured and tested in compliance with this standard shall not be used to handle, offer for transport or transport dangerous goods if the contents meet the packing group I criteria specified in Part 2, Classification, of the TDG Regulations.', N'Un contenant fabriqué et mis à l’essai conformément aux exigences de la présente norme ne doit pas être utilisé pour la présentation au transport, la manutention ou le transport de marchandises dangereuses si son contenu satisfait aux critères du groupe d’emballage I, prescrits dans la partie 2, Classification, du Règlement sur le TMD.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'924DB004-B73C-410A-8C10-4DEC60A6307F', NULL, NULL, N'CGSB-43.123 8.1.9.2', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'A TC-2R container manufactured and tested in compliance with this standard with a maximum capacity greater than 675 mL shall not be used to handle, offer for transport or transport dangerous goods if the contents meet the Class 2.1 criteria specified in Part 2, Classification, of the TDG Regulations.', N'Clause', N'CGSB-43.123 8.1.9.2', N'TDGA 5 (c)', N'TDGR 5.11', N'3128', N'Un contenant TC-2R fabriqué et mis à l’essai conformément aux exigences de la présente norme et doté d’une capacité maximale supérieure à 675 mL ne doit pas être utilisé pour la manutention, la demande de transport ou le transport de marchandises dangereuses si son contenu satisfait aux critères de la classe 2.1, prescrits dans la partie 2, Classification, du Règlement sur le TMD.', N'A TC-2R container manufactured and tested in compliance with this standard with a maximum capacity greater than 675 mL shall not be used to handle, offer for transport or transport dangerous goods if the contents meet the Class 2.1 criteria specified in Part 2, Classification, of the TDG Regulations.', N'Un contenant TC-2R fabriqué et mis à l’essai conformément aux exigences de la présente norme et doté d’une capacité maximale supérieure à 675 mL ne doit pas être utilisé pour la manutention, la demande de transport ou le transport de marchandises dangereuses si son contenu satisfait aux critères de la classe 2.1, prescrits dans la partie 2, Classification, du Règlement sur le TMD.', NULL)
GO
