INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'40F9DCE7-5A11-4B28-AED8-31B890152BBF', NULL, NULL, N'CGSB-43.125 4.3.3', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Whatever the intended temperature of the consignment, the primary receptacle or the secondary inner packaging of a type P620 packaging shall be capable of withstanding temperatures in the range of -40 to +55°C.', N'Clause', N'CGSB-43.125 4.3.3', N'TDGA 5 (c)', NULL, N'3144', N'Peu importe la température prévue de l’envoi, le récipient primaire ou l’emballage intérieur secondaire d’un emballage de type P620 doit pouvoir résister à une température de -40 à 55 °C.', N'Whatever the intended temperature of the consignment, the primary receptacle or the secondary inner packaging of a type P620 packaging shall be capable of withstanding temperatures in the range of -40 to +55°C.', N'Peu importe la température prévue de l’envoi, le récipient primaire ou l’emballage intérieur secondaire d’un emballage de type P620 doit pouvoir résister à une température de -40 à 55 °C.', NULL)
