INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'EF665814-CE7B-4A38-ACBF-9EFBD57DCF67', NULL, NULL, N'CGSB-43.125 4.3.4', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'The primary receptacle and the secondary inner packaging of packagings intended to contain a refrigerant shall maintain their integrity at the temperature of the refrigerant used as well as at the temperatures and the pressures which could result if refrigeration were lost.', N'Clause', N'CGSB-43.125 4.3.4', N'TDGA 5 (c)', NULL, N'3145', N'Le récipient primaire et l’emballage intérieur secondaire des emballages destinés à contenir un réfrigérant doiventconserver leur intégrité à la température du réfrigérant utilisé de même qu’aux températures et aux pressions pouvant découler d’une perte de réfrigérant.', N'The primary receptacle and the secondary inner packaging of packagings intended to contain a refrigerant shall maintain their integrity at the temperature of the refrigerant used as well as at the temperatures and the pressures which could result if refrigeration were lost.', N'Le récipient primaire et l’emballage intérieur secondaire des emballages destinés à contenir un réfrigérant doiventconserver leur intégrité à la température du réfrigérant utilisé de même qu’aux températures et aux pressions pouvant découler d’une perte de réfrigérant.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'1CCAD01A-60DE-4F3E-856B-30115C369843', NULL, NULL, N'CGSB-43.125 4.4.1 a)', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'When made available as a kit, the packaging manufacturer and subsequent distributor shall provide the following information to the customer in relation to each packaging design: a procedure and a list of components with sufficient information to allow the user to assemble, fill and close the packaging in the same fashion as it was tested.', N'Clause', N'CGSB-43.125 4.4.1 a)', N'TDGA 5 (c)', NULL, N'3146', N'Lorsque l’emballage est fourni sous forme de prêt-à-monter, le fabricant et le distributeur subséquent doivent fournir les renseignements suivants au client sur chaque modèle d’emballage : une procédure et une liste des composantes avec suffisamment d’information pour que l’utilisateur puisse assembler, remplir et fermer les emballages de la même façon qu’ils ont été éprouvés.', N'When made available as a kit, the packaging manufacturer and subsequent distributor shall provide the following information to the customer in relation to each packaging design: a procedure and a list of components with sufficient information to allow the user to assemble, fill and close the packaging in the same fashion as it was tested.', N'Lorsque l’emballage est fourni sous forme de prêt-à-monter, le fabricant et le distributeur subséquent doivent fournir les renseignements suivants au client sur chaque modèle d’emballage : une procédure et une liste des composantes avec suffisamment d’information pour que l’utilisateur puisse assembler, remplir et fermer les emballages de la même façon qu’ils ont été éprouvés.', NULL)
GO
