INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'82F303B7-BF98-4CF3-A51B-04D84F001BEF', NULL, NULL, N'CGSB-43.125 5.1.1.2.1', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'For packagings with a gross mass of more than 30 kg, the marking (or a duplicate thereof) shall appear on the top or side of the packaging. For drums and jerricans with a removable head, the markings shall appear on the side.', N'Clause', N'CGSB-43.125 5.1.1.2.1', N'TDGA 5 (c)', NULL, N'3152', N'Dans le cas des emballages ayant une masse brute supérieure à 30 kg, les marques (ou une reproduction de celles-ci) doivent être apposées sur le dessus ou le côté de l’emballage. Dans le cas des fûts et des bidons dont le dessus est amovible, les marques doivent être apposées sur le côté de l’emballage.', N'For packagings with a gross mass of more than 30 kg, the marking (or a duplicate thereof) shall appear on the top or side of the packaging. For drums and jerricans with a removable head, the markings shall appear on the side.', N'Dans le cas des emballages ayant une masse brute supérieure à 30 kg, les marques (ou une reproduction de celles-ci) doivent être apposées sur le dessus ou le côté de l’emballage. Dans le cas des fûts et des bidons dont le dessus est amovible, les marques doivent être apposées sur le côté de l’emballage.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'0E2F3640-11A8-40D5-BED2-C2BFDC9AB2D7', NULL, NULL, N'CGSB-43.125 5.1.1.2.2', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'For packagings with a gross mass of 30 kg or less, the marking (or a duplicate thereof) shall appear on the top, side or bottom. For drums and jerricans with a removable head, the markings shall appear on the side or bottom.', N'Clause', N'CGSB-43.125 5.1.1.2.2', N'TDGA 5 (c)', NULL, N'3153', N'Dans le cas des emballages ayant une masse brute de 30 kg ou moins, les marques (ou une reproduction de celles-ci) doivent être apposées sur le dessus, le côté ou le fond de l’emballage. Dans le cas des fûts et des bidons dont le dessus est amovible, les marques doivent être apposées sur le côté ou le fond de l’emballage.', N'For packagings with a gross mass of 30 kg or less, the marking (or a duplicate thereof) shall appear on the top, side or bottom. For drums and jerricans with a removable head, the markings shall appear on the side or bottom.', N'Dans le cas des emballages ayant une masse brute de 30 kg ou moins, les marques (ou une reproduction de celles-ci) doivent être apposées sur le dessus, le côté ou le fond de l’emballage. Dans le cas des fûts et des bidons dont le dessus est amovible, les marques doivent être apposées sur le côté ou le fond de l’emballage.', NULL)
GO
