INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'49C16F82-E09E-49CC-879C-C190EDEAB05A', NULL, NULL, N'CGSB-43.125 7.3.1.2', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Where the packaging is intended to contain dry ice, one additional drop test (refer to Tables 2A and 2B) shall be carried out.', N'Clause', N'CGSB-43.125 7.3.1.2', N'TDGA 5 (c)', NULL, N'3203', N'Lorsque l’emballage est destiné à contenir de la neige carbonique, un essai de chute additionnel (se reporter aux tableaux 2A et 2B) doit être mené.', N'Where the packaging is intended to contain dry ice, one additional drop test (refer to Tables 2A and 2B) shall be carried out.', N'Lorsque l’emballage est destiné à contenir de la neige carbonique, un essai de chute additionnel (se reporter aux tableaux 2A et 2B) doit être mené.', NULL)
