INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'3BDFA849-3B74-4845-99F0-C75A7B8A6763', NULL, NULL, N'CGSB-43.125 5.2.1.3', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'The marking shall be in the form of a square set at an angle of 45° (diamond-shaped) with each side having a length of at least 50 mm; the width of the line shall be at least 2 mm and the letters and numbers shall be at least 6 mm high.', N'Clause', N'CGSB-43.125 5.2.1.3', N'TDGA 5 (c)', NULL, N'3169', N'Les marques doivent avoir la forme d’un carré posé à un angle de 45° (losange), chaque côté ayant au moins 50 mm de longueur; l’épaisseur de la ligne doit être d’au moins 2 mm et les lettres et les chiffres doivent avoir au moins 6 mm de hauteur.', N'The marking shall be in the form of a square set at an angle of 45° (diamond-shaped) with each side having a length of at least 50 mm; the width of the line shall be at least 2 mm and the letters and numbers shall be at least 6 mm high.', N'Les marques doivent avoir la forme d’un carré posé à un angle de 45° (losange), chaque côté ayant au moins 50 mm de longueur; l’épaisseur de la ligne doit être d’au moins 2 mm et les lettres et les chiffres doivent avoir au moins 6 mm de hauteur.', NULL)
