INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'D95B2C8F-5D5E-478A-9849-A070A6977BE1', NULL, NULL, N'CGSB-43.125 6.1.1 a) 1)', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'The packaging shall consist of the following components: inner packagings comprising leakproof primary receptacle(s).', N'Clause', N'CGSB-43.125 6.1.1 a) 1)', N'TDGA 5 (c)', NULL, N'3170', N'L’emballage doit être constitué des composants suivants : des emballages intérieurs comportant un ou des récipients primaires étanches.', N'The packaging shall consist of the following components: inner packagings comprising leakproof primary receptacle(s).', N'L’emballage doit être constitué des composants suivants : des emballages intérieurs comportant un ou des récipients primaires étanches.', NULL)
