INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'A353A83E-FA97-4F9E-9443-48EC15C2D775', NULL, NULL, N'CGSB-43.125 6.1.1 b)', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'The packaging shall consist of the following components: a rigid outer packaging of adequate strength for its capacity, mass and intended use of which the smallest external dimension is at least 100 mm. The outer packaging shall be selected from Table 1.', N'Clause', N'CGSB-43.125 6.1.1 b)', N'TDGA 5 (c)', NULL, N'3172', N'L’emballage doit être constitué des composants suivants : un emballage extérieur rigide d’une résistance correspondant à sa contenance, à sa masse et à l’utilisation prévue dont la dimension externe la plus petite est d’au moins 100 mm. L’emballage extérieur doit être choisi parmi les emballages du tableau 1.', N'The packaging shall consist of the following components: a rigid outer packaging of adequate strength for its capacity, mass and intended use of which the smallest external dimension is at least 100 mm. The outer packaging shall be selected from Table 1.', N'L’emballage doit être constitué des composants suivants : un emballage extérieur rigide d’une résistance correspondant à sa contenance, à sa masse et à l’utilisation prévue dont la dimension externe la plus petite est d’au moins 100 mm. L’emballage extérieur doit être choisi parmi les emballages du tableau 1.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'343FF8C7-D336-4ABD-A1BF-32A4C9DDDB92', NULL, NULL, N'CGSB-43.125 6.1.2', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'For liquid infectious substances, an absorbent material shall be placed between the primary receptacle(s) and the secondary inner packaging and in sufficient quantity to absorb the entire content of the primary receptacle(s).', N'Clause', N'CGSB-43.125 6.1.2', N'TDGA 5 (c)', NULL, N'3173', N'Dans le cas de matières infectieuses liquides, un matériau absorbant doit être placé entre le ou les récipients primaires et l’emballage intérieur secondaire en quantité suffisante pour absorber la totalité du liquide contenu dans le ou les récipients primaires.', N'For liquid infectious substances, an absorbent material shall be placed between the primary receptacle(s) and the secondary inner packaging and in sufficient quantity to absorb the entire content of the primary receptacle(s).', N'Dans le cas de matières infectieuses liquides, un matériau absorbant doit être placé entre le ou les récipients primaires et l’emballage intérieur secondaire en quantité suffisante pour absorber la totalité du liquide contenu dans le ou les récipients primaires.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'A9E91EB5-9189-432B-9FA3-88A681AF26DA', NULL, NULL, N'CGSB-43.125 6.1.3 a)', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Primary receptacles intended for the transportation of substances consigned at ambient temperature or at a higher temperature shall be made of glass, metal or plastic.', N'Clause', N'CGSB-43.125 6.1.3 a)', N'TDGA 5 (c)', NULL, N'3174', N'Les récipients primaires destinés à transporter des matières expédiées à la température ambiante ou à une température plus élevée doivent être en verre, en métal ou en plastique.', N'Primary receptacles intended for the transportation of substances consigned at ambient temperature or at a higher temperature shall be made of glass, metal or plastic.', N'Les récipients primaires destinés à transporter des matières expédiées à la température ambiante ou à une température plus élevée doivent être en verre, en métal ou en plastique.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'511E72EC-EE57-45A0-A8DB-6E1E13C5828F', NULL, NULL, N'CGSB-43.125 6.1.3 b)', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Primary receptacles intended for the transportation of substances consigned in liquid nitrogen shall be made of plastic and capable of withstanding very low temperature.', N'Clause', N'CGSB-43.125 6.1.3 b)', N'TDGA 5 (c)', NULL, N'3175', N'Les récipients primaires destinés à transporter des matières expédiées dans de l’azote liquide doivent être en plastique et pouvoir résister à des températures très basses.', N'Primary receptacles intended for the transportation of substances consigned in liquid nitrogen shall be made of plastic and capable of withstanding very low temperature.', N'Les récipients primaires destinés à transporter des matières expédiées dans de l’azote liquide doivent être en plastique et pouvoir résister à des températures très basses.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'2728F3BD-E4BF-4C70-ABE2-296A08538178', NULL, NULL, N'CGSB-43.125 7.3.1.4', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Except for flat drops, the centre of gravity shall be vertically over the point of impact.', N'Clause', N'CGSB-43.125 7.3.1.4', N'TDGA 5 (c)', NULL, N'3204', N'Sauf dans le cas des essais de chute en position horizontale, le centre de gravité doit être vertical audessus du point d’impact.', N'Except for flat drops, the centre of gravity shall be vertically over the point of impact.', N'Sauf dans le cas des essais de chute en position horizontale, le centre de gravité doit être vertical audessus du point d’impact.', NULL)
