INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'A77AB674-9607-491C-A265-A05CC4CEACC3', NULL, NULL, N'CGSB-43.125 6.1.3 c)', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Primary receptacles intended for the transportation of lyophilized substances may be consigned in flame-sealed glass ampoules or rubber-stoppered glass vials fitted with metal seals.', N'Clause', N'CGSB-43.125 6.1.3 c)', N'TDGA 5 (c)', NULL, N'3176', N'Les récipients primaires destinés à transporter les matières lyophilisées peuvent être expédiées dans des ampoules de verre scellées à la flamme ou des flacons de verre à bouchon de caoutchouc scellés par une capsule métallique.', N'Primary receptacles intended for the transportation of lyophilized substances may be consigned in flame-sealed glass ampoules or rubber-stoppered glass vials fitted with metal seals.', N'Les récipients primaires destinés à transporter les matières lyophilisées peuvent être expédiées dans des ampoules de verre scellées à la flamme ou des flacons de verre à bouchon de caoutchouc scellés par une capsule métallique.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'BB92D1FF-713D-4DC8-BE07-E781C5B12889', NULL, NULL, N'CGSB-43.125 6.1.4', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'For substances consigned at ambient or higher temperatures, positive means of ensuring a leakproof seal shall be provided, e.g. heat seal, a skirted stopper or a metal crimp seal. If screw caps are used, they shall be secured by positive means, e.g, tape, paraffin sealing tape or manufactured locking closure.', N'Clause', N'CGSB-43.125 6.1.4', N'TDGA 5 (c)', NULL, N'3177', N'Dans le cas des matières expédiées à la température ambiante ou à une température plus élevée, un dispositif d’étanchéité pour l’emballage doit être fourni, comme le thermosoudage, un bouchon à jupe ou une capsule métallique sertie. Si des bouchons filetés sont utilisés, on doit les assujetir par des moyens de blocage efficaces comme du ruban adhésif, une bande de parafine ou une fermeture verrouillable fabriquée à cet effet.', N'For substances consigned at ambient or higher temperatures, positive means of ensuring a leakproof seal shall be provided, e.g. heat seal, a skirted stopper or a metal crimp seal. If screw caps are used, they shall be secured by positive means, e.g, tape, paraffin sealing tape or manufactured locking closure.', N'Dans le cas des matières expédiées à la température ambiante ou à une température plus élevée, un dispositif d’étanchéité pour l’emballage doit être fourni, comme le thermosoudage, un bouchon à jupe ou une capsule métallique sertie. Si des bouchons filetés sont utilisés, on doit les assujetir par des moyens de blocage efficaces comme du ruban adhésif, une bande de parafine ou une fermeture verrouillable fabriquée à cet effet.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'2CAE97F1-54A6-446B-AB7C-3B5BDFCDA1EE', NULL, NULL, N'CGSB-43.125 6.1.5', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'If multiple fragile primary receptacles are placed in a secondary inner packaging, the primary receptacles shall be individually wrapped or otherwise separated to prevent contact between them.', N'Clause', N'CGSB-43.125 6.1.5', N'TDGA 5 (c)', NULL, N'3178', N'Lorsque plusieurs récipients primaires fragiles sont placés dans un emballage intérieur secondaire, les récipients primaires doivent être emballés individuellement ou autrement séparés pour empêcher un contact entre les récipients.', N'If multiple fragile primary receptacles are placed in a secondary inner packaging, the primary receptacles shall be individually wrapped or otherwise separated to prevent contact between them.', N'Lorsque plusieurs récipients primaires fragiles sont placés dans un emballage intérieur secondaire, les récipients primaires doivent être emballés individuellement ou autrement séparés pour empêcher un contact entre les récipients.', NULL)
