INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'45F235C5-371B-42D5-ABB6-8E59159612B6', NULL, NULL, N'CGSB-43.125 6.2.5 b)', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Packaging intended to contain a refrigerant such as ice, dry ice or liquid nitrogen shall conform to the following requirements: interior supports shall be provided to secure secondary inner packaging(s) or outer packaging(s) in the original position after the refrigerant has dissipated.', N'Clause', N'CGSB-43.125 6.2.5 b)', N'TDGA 5 (c)', NULL, N'3194', N'Les emballages destinés à contenir un réfrigérant comme de la glace, de la neige carbonique ou de l’azote liquide doivent être conformes aux exigences suivantes : des supports intérieurs doivent être fournis pour maintenir le ou les emballages intérieurs secondaires ou les emballages extérieurs dans leur position initiale après la dissipation du réfrigérant.', N'Packaging intended to contain a refrigerant such as ice, dry ice or liquid nitrogen shall conform to the following requirements: interior supports shall be provided to secure secondary inner packaging(s) or outer packaging(s) in the original position after the refrigerant has dissipated.', N'Les emballages destinés à contenir un réfrigérant comme de la glace, de la neige carbonique ou de l’azote liquide doivent être conformes aux exigences suivantes : des supports intérieurs doivent être fournis pour maintenir le ou les emballages intérieurs secondaires ou les emballages extérieurs dans leur position initiale après la dissipation du réfrigérant.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'C93AFD5A-F7C1-4AED-9F72-183BBD2A2357', NULL, NULL, N'CGSB-43.125 6.2.5 c)', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Packaging intended to contain a refrigerant such as ice, dry ice or liquid nitrogen shall conform to the following requirements: in the case of ice, the outer packaging or overpack shall be leakproof.', N'Clause', N'CGSB-43.125 6.2.5 c)', N'TDGA 5 (c)', NULL, N'3195', N'Les emballages destinés à contenir un réfrigérant comme de la glace, de la neige carbonique ou de l’azote liquide doivent être conformes aux exigences suivantes : dans le cas de la glace, l’emballage extérieur ou le suremballage doit être étanche.', N'Packaging intended to contain a refrigerant such as ice, dry ice or liquid nitrogen shall conform to the following requirements: in the case of ice, the outer packaging or overpack shall be leakproof.', N'Les emballages destinés à contenir un réfrigérant comme de la glace, de la neige carbonique ou de l’azote liquide doivent être conformes aux exigences suivantes : dans le cas de la glace, l’emballage extérieur ou le suremballage doit être étanche.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'4B14A068-43ED-497A-93F3-74B0F5C90278', NULL, NULL, N'CGSB-43.125 6.2.5 d)', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Packaging intended to contain a refrigerant such as ice, dry ice or liquid nitrogen shall conform to the following requirements: in the case of dry ice or liquid nitrogen, the outer packaging or overpack shall permit the release of gas.', N'Clause', N'CGSB-43.125 6.2.5 d)', N'TDGA 5 (c)', NULL, N'3196', N'Les emballages destinés à contenir un réfrigérant comme de la glace, de la neige carbonique ou de l’azote liquide doivent être conformes aux exigences suivantes : dans le cas de la neige carbonique ou de l’azote liquide, l’emballage extérieur ou le suremballage doit permettre le dégagement du gaz.', N'Packaging intended to contain a refrigerant such as ice, dry ice or liquid nitrogen shall conform to the following requirements: in the case of dry ice or liquid nitrogen, the outer packaging or overpack shall permit the release of gas.', N'Les emballages destinés à contenir un réfrigérant comme de la glace, de la neige carbonique ou de l’azote liquide doivent être conformes aux exigences suivantes : dans le cas de la neige carbonique ou de l’azote liquide, l’emballage extérieur ou le suremballage doit permettre le dégagement du gaz.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'451BA228-B6E8-46C7-A6B8-E0120F9E7102', NULL, NULL, N'CGSB-43.125 7.1.1', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Packagings selected for testing shall be representative of the design intended for production. The tests required for type P620 packagings are set out in 7.3 to 7.5 and Table 2A of this standard, and the tests required for type P650 packagings are set out in 7.3 and 7.5 (if applicable) and Table 2B of this standard.', N'Clause', N'CGSB-43.125 7.1.1', N'TDGA 5 (c)', NULL, N'3197', N'Les emballages sélectionnés pour les essais doivent être représentatifs du modèle de production prévu. Les essais
requis pour les emballages de type P620 sont mentionnés en 7.3 à 7.5 et au tableau 2A de la présente norme et les essais requis pour les emballages de type P650 sont indiqués en 7.3 et 7.5 (le cas échéant) et au tableau 2B de la présente norme.', N'Packagings selected for testing shall be representative of the design intended for production. The tests required for type P620 packagings are set out in 7.3 to 7.5 and Table 2A of this standard, and the tests required for type P650 packagings are set out in 7.3 and 7.5 (if applicable) and Table 2B of this standard.', N'Les emballages sélectionnés pour les essais doivent être représentatifs du modèle de production prévu. Les essais
requis pour les emballages de type P620 sont mentionnés en 7.3 à 7.5 et au tableau 2A de la présente norme et les essais requis pour les emballages de type P650 sont indiqués en 7.3 et 7.5 (le cas échéant) et au tableau 2B de la présente norme.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'2F8EF894-CCDF-4C0D-B058-605265D923B7', NULL, NULL, N'CGSB-43.125 7.1.2', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Tests shall be repeated after each variation of the design, material or manner of construction of a packaging unless the variations are permitted in section 8. Design variations shall be documented in the design report.', N'Clause', N'CGSB-43.125 7.1.2', N'TDGA 5 (c)', NULL, N'3198', N'Les essais doivent être répétés pour chaque variante de conception, de matériau ou de la méthode de fabrication d’un emballage sauf si les variantes sont permises conformément à la section 8. Les variantes de conception doivent être documentées dans le rapport sur le modèle.', N'Tests shall be repeated after each variation of the design, material or manner of construction of a packaging unless the variations are permitted in section 8. Design variations shall be documented in the design report.', N'Les essais doivent être répétés pour chaque variante de conception, de matériau ou de la méthode de fabrication d’un emballage sauf si les variantes sont permises conformément à la section 8. Les variantes de conception doivent être documentées dans le rapport sur le modèle.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'9FCAE8E9-A5FA-402A-93E5-CF8274A63EF0', NULL, NULL, N'CGSB-43.125 7.2.1', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Fill and close the packagings for testing in the same manner as for transport. All closures shall be installed as specified by the closure manufacturer or packaging manufacturer.', N'Clause', N'CGSB-43.125 7.2.1', N'TDGA 5 (c)', NULL, N'3199', N'Remplir et fermer les emballages pour les essais à l’aide de la même méthode que celle qui sera utilisée pour le transport. Toutes les fermetures doivent être installées conformément aux directives du fabricant de la fermeture ou du fabricant de l’emballage.', N'Fill and close the packagings for testing in the same manner as for transport. All closures shall be installed as specified by the closure manufacturer or packaging manufacturer.', N'Remplir et fermer les emballages pour les essais à l’aide de la même méthode que celle qui sera utilisée pour le transport. Toutes les fermetures doivent être installées conformément aux directives du fabricant de la fermeture ou du fabricant de l’emballage.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'D88FD3DA-0F04-43DD-B4DF-DE5045C9F471', NULL, NULL, N'CGSB-43.125 7.2.2', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Each primary receptacle shall be filled to not less than 98% of its capacity. Liquid or solid infectious substance shall be replaced by water or, where conditioning at –18°C is specified, by water/antifreeze with a minimum specific gravity of 0.95.', N'Clause', N'CGSB-43.125 7.2.2', N'TDGA 5 (c)', NULL, N'3200', N'Chaque récipient primaire doit être rempli à au moins 98 % de sa contenance. La matière infectieuse liquide ou solide doit être remplacée par de l’eau ou, lorsqu’un conditionnement à –18 °C est spécifié, par un mélange d’eau et d’antigel d’une densité minimale de 0,95.', N'Each primary receptacle shall be filled to not less than 98% of its capacity. Liquid or solid infectious substance shall be replaced by water or, where conditioning at –18°C is specified, by water/antifreeze with a minimum specific gravity of 0.95.', N'Chaque récipient primaire doit être rempli à au moins 98 % de sa contenance. La matière infectieuse liquide ou solide doit être remplacée par de l’eau ou, lorsqu’un conditionnement à –18 °C est spécifié, par un mélange d’eau et d’antigel d’une densité minimale de 0,95.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'62EC8B4E-7CA4-4DD8-95C7-1B217666F37F', NULL, NULL, N'CGSB-43.125 7.3', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Types P620 and P650 packagings shall be subjected to the drop test in accordance with this section.', N'Clause', N'CGSB-43.125 7.3', N'TDGA 5 (c)', NULL, N'3201', N'Les emballages des types P620 et P650 doivent être soumis à un essai de chute conformément au présent paragraphe.', N'Types P620 and P650 packagings shall be subjected to the drop test in accordance with this section.', N'Les emballages des types P620 et P650 doivent être soumis à un essai de chute conformément au présent paragraphe.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'E0A135F5-66B0-431B-B692-233785ACB776', NULL, NULL, N'CGSB-43.125 7.3.1.1', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Perform the drop test in accordance with ASTM D5276 using the appropriate drop orientation as specified
in 7.3.5 and the appropriate number of packagings in accordance with Tables 2A and 2B. Where more than one
orientation is possible for a given drop test, the orientation most likely to result in failure of the packaging shall be
used.', N'Clause', N'CGSB-43.125 7.3.1.1', N'TDGA 5 (c)', NULL, N'3202', N'Réaliser l’essai de chute conformément à D5276 de l’ASTM avec la chute orientée de manière appropriée
comme spécifiée en 7.3.5 et le nombre approprié d’emballages conformément aux tableaux 2A et 2B. Lorsque
plus d’une orientation est possible pour un essai de chute donné, l’orientation la plus susceptible d’entraîner une
défaillance de l’emballage doit être utilisée.', N'Perform the drop test in accordance with ASTM D5276 using the appropriate drop orientation as specified
in 7.3.5 and the appropriate number of packagings in accordance with Tables 2A and 2B. Where more than one
orientation is possible for a given drop test, the orientation most likely to result in failure of the packaging shall be
used.', N'Réaliser l’essai de chute conformément à D5276 de l’ASTM avec la chute orientée de manière appropriée
comme spécifiée en 7.3.5 et le nombre approprié d’emballages conformément aux tableaux 2A et 2B. Lorsque
plus d’une orientation est possible pour un essai de chute donné, l’orientation la plus susceptible d’entraîner une
défaillance de l’emballage doit être utilisée.', NULL)
GO
