INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'114EDA47-F994-46F9-8A56-407EC172D85E', NULL, NULL, N'CGSB-43.125 7.3.2.1', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Packagings requiring ambient temperature conditioning shall be conditioned in accordance with ASTM D4332.', N'Clause', N'CGSB-43.125 7.3.2.1', N'TDGA 5 (c)', NULL, N'3207', N'Les emballages exigeant un conditionnement à température ambiante doivent être conditionnés conformément à D4332 de l’ASTM.', N'Packagings requiring ambient temperature conditioning shall be conditioned in accordance with ASTM D4332.', N'Les emballages exigeant un conditionnement à température ambiante doivent être conditionnés conformément à D4332 de l’ASTM.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'CDAE9240-BBB8-4DA2-8231-FB2F446218BA', NULL, NULL, N'CGSB-43.125 7.3.2.2.1', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Subject the packagings to a water spray that simulates exposure to a rainfall of approximately 5 cm per hour for at least one hour in accordance with ASTM D951.', N'Clause', N'CGSB-43.125 7.3.2.2.1', N'TDGA 5 (c)', NULL, N'3208', N'Soumettre les emballages à une aspersion d’eau qui simule une exposition à une pluie correspondant à environ 5 cm par heure pendant au moins une heure conformément à D951 de l’ASTM.', N'Subject the packagings to a water spray that simulates exposure to a rainfall of approximately 5 cm per hour for at least one hour in accordance with ASTM D951.', N'Soumettre les emballages à une aspersion d’eau qui simule une exposition à une pluie correspondant à environ 5 cm par heure pendant au moins une heure conformément à D951 de l’ASTM.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'E21D9031-DF6E-4FE9-8E0E-D939BF7DDCB8', NULL, NULL, N'CGSB-43.125 7.3.2.2.2', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'Subject the packagings to a temperature of ‑18°C or lower for a period of not less than 24 h in accordance with ASTM D4332. If the sample contains dry ice, then the conditioning period may be reduced to 4 h.', N'Clause', N'CGSB-43.125 7.3.2.2.2', N'TDGA 5 (c)', NULL, N'3209', N'Soumettre l’emballage à une température de ‑18 °C ou à une température inférieure pendant au moins 24 h conformément à D4332 de l’ASTM. Si l’échantillon contient de la neige carbonique, la période de conditionnement peut alors être réduite à 4 h.', N'Subject the packagings to a temperature of ‑18°C or lower for a period of not less than 24 h in accordance with ASTM D4332. If the sample contains dry ice, then the conditioning period may be reduced to 4 h.', N'Soumettre l’emballage à une température de ‑18 °C ou à une température inférieure pendant au moins 24 h conformément à D4332 de l’ASTM. Si l’échantillon contient de la neige carbonique, la période de conditionnement peut alors être réduite à 4 h.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'4A45AE1B-FBC3-4553-8A44-4842D9FB73F9', NULL, NULL, N'CGSB-43.125 7.3.2.2.3', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'One packaging shall be stored until all the dry ice has dissipated.', N'Clause', N'CGSB-43.125 7.3.2.2.3', N'TDGA 5 (c)', NULL, N'3210', N'Un emballage doit être entreposé jusqu’à ce que toute la neige carbonique se dissipe.', N'One packaging shall be stored until all the dry ice has dissipated.', N'Un emballage doit être entreposé jusqu’à ce que toute la neige carbonique se dissipe.', NULL)
GO
