INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'3A327DE2-5314-4D5A-8BC2-59891E28120D', NULL, NULL, N'CGSB-43.145 5.1.5 a)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The letter “T”, identifying a salvage large packaging, shall not be assigned to the packaging code unless the representative prototype was successfully tested to the packing group I or II performance level and marked with the letter “X” or “Y” in accordance with 5.1.1 c).', N'Clause', N'CGSB-43.145 5.1.5 a)', NULL, NULL, N'3286', N'La lettre « T », qui désigne un grand emballage de secours, ne sera pas comprise dans le code de l’emballage, à moins que le prototype représentatif du grand emballage ait réussi l’essai pour les niveaux de performance des groupes d’emballage I ou II, et qu’il ait été marqué des lettres « X » ou « Y », conformément à 5.1.1c).', N'The letter “T”, identifying a salvage large packaging, shall not be assigned to the packaging code unless the representative prototype was successfully tested to the packing group I or II performance level and marked with the letter “X” or “Y” in accordance with 5.1.1 c).', N'La lettre « T », qui désigne un grand emballage de secours, ne sera pas comprise dans le code de l’emballage, à moins que le prototype représentatif du grand emballage ait réussi l’essai pour les niveaux de performance des groupes d’emballage I ou II, et qu’il ait été marqué des lettres « X » ou « Y », conformément à 5.1.1c).', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'077DF71A-9D49-4DE3-B1D7-793CE1A56AE1', NULL, NULL, N'CGSB-43.145 5.1.5 b)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The letter “T”, identifying a salvage large packaging, shall not be assigned to the packaging code unless the representative prototype was successfully tested in accordance with clause 7 with water and filled to not less than 98% of its maximum capacity and to its maximum permissible gross mass. ', N'Clause', N'CGSB-43.145 5.1.5 b)', NULL, NULL, N'3287', N'La lettre « T », qui désigne un grand emballage de secours, ne sera pas comprise dans le code de l’emballage, à moins que le prototype représentatif du grand emballage ait réussi l’essai de la section 7 en étant rempli d’eau à au moins 98 % de sa contenance ainsi qu’à sa masse brute maximale admissible', N'The letter “T”, identifying a salvage large packaging, shall not be assigned to the packaging code unless the representative prototype was successfully tested in accordance with clause 7 with water and filled to not less than 98% of its maximum capacity and to its maximum permissible gross mass. ', N'La lettre « T », qui désigne un grand emballage de secours, ne sera pas comprise dans le code de l’emballage, à moins que le prototype représentatif du grand emballage ait réussi l’essai de la section 7 en étant rempli d’eau à au moins 98 % de sa contenance ainsi qu’à sa masse brute maximale admissible', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'86474A28-3F9B-4AF7-865E-DB5200136B1F', NULL, NULL, N'CGSB-43.145 5.1.5 c)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The letter “T”, identifying a salvage large packaging, shall not be assigned to the packaging code unless the large packaging has passed the leakproofness test set out in 7.8 at a test pressure of 30 kPa.', N'Clause', N'CGSB-43.145 5.1.5 c)', NULL, NULL, N'3288', N'La lettre « T », qui désigne un grand emballage de secours, ne sera pas comprise dans le code de l’emballage, à moins que le grand emballage ait réussi l’essai d’étanchéité en 7.8 à une pression d’essai de 30 kPa.', N'The letter “T”, identifying a salvage large packaging, shall not be assigned to the packaging code unless the large packaging has passed the leakproofness test set out in 7.8 at a test pressure of 30 kPa.', N'La lettre « T », qui désigne un grand emballage de secours, ne sera pas comprise dans le code de l’emballage, à moins que le grand emballage ait réussi l’essai d’étanchéité en 7.8 à une pression d’essai de 30 kPa.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'9B74B62C-6934-4207-8FA6-314BA0A53D18', NULL, NULL, N'CGSB-43.145 5.2.1.1', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The maximum permissible stacking load applicable shall be displayed on a symbol as shown in 5.2.1.1.1 or 5.2.1.1.2. The symbol and lettering shall be applied against a background of contrasting colour. The length of any side of the symbol as displayed on a large packaging shall be equal to or greater than 100 mm. The letters and numbers shall be at least 12 mm high.', N'Clause', N'CGSB-43.145 5.2.1.1', NULL, NULL, N'3289', N'La charge de gerbage maximale admissible qui s’applique doit être indiquée sous forme de symbole, conformément à 5.2.1.1.1 ou à 5.2.1.1.2. Le symbole et les caractères doivent être inscrits sur un fond d’une couleur contrastante. La longueur des côtés du symbole paraissant sur le grand emballage doit être égale ou supérieure à 100 mm. Les lettres et les chiffres doivent mesurer au moins 12 mm de hauteur.', N'The maximum permissible stacking load applicable shall be displayed on a symbol as shown in 5.2.1.1.1 or 5.2.1.1.2. The symbol and lettering shall be applied against a background of contrasting colour. The length of any side of the symbol as displayed on a large packaging shall be equal to or greater than 100 mm. The letters and numbers shall be at least 12 mm high.', N'La charge de gerbage maximale admissible qui s’applique doit être indiquée sous forme de symbole, conformément à 5.2.1.1.1 ou à 5.2.1.1.2. Le symbole et les caractères doivent être inscrits sur un fond d’une couleur contrastante. La longueur des côtés du symbole paraissant sur le grand emballage doit être égale ou supérieure à 100 mm. Les lettres et les chiffres doivent mesurer au moins 12 mm de hauteur.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'8A156EAB-F751-4B50-96B9-BBEAFC90876D', NULL, NULL, N'CGSB-43.145 5.2.2', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'When a UN design type is converted to another UN design type, the original primary marking and additional markings specified in 5.1.1 and 5.2.1 shall be removed or made permanently illegible and new primary marking and additional markings shall be applied in accordance with this standard.', N'Clause', N'CGSB-43.145 5.2.2', NULL, NULL, N'3290', N'Lorsqu’un modèle type normalisé UN est reconverti en un autre modèle normalisé UN, le marquage de base original, et tout marquage additionnel défini en 5.1.1 et 5.2.1, sera retiré ou rendu illisible de manière permanente et un nouveau marquage de base et marquage additionnel sera apposé, conformément à la présente norme.', N'When a UN design type is converted to another UN design type, the original primary marking and additional markings specified in 5.1.1 and 5.2.1 shall be removed or made permanently illegible and new primary marking and additional markings shall be applied in accordance with this standard.', N'Lorsqu’un modèle type normalisé UN est reconverti en un autre modèle normalisé UN, le marquage de base original, et tout marquage additionnel défini en 5.1.1 et 5.2.1, sera retiré ou rendu illisible de manière permanente et un nouveau marquage de base et marquage additionnel sera apposé, conformément à la présente norme.', NULL)
GO
