INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'76E49F62-526B-4C59-9CCA-082DAB014C0E', NULL, NULL, N'CGSB-43.145 5.2.3', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Salvage large packagings shall additionally be marked with the word “SALVAGE” or “SECOURS” near the marks specified in 5.1.1. The lettering of the “SALVAGE” or “SECOURS” mark shall be at least 12 mm high.', N'Clause', N'CGSB-43.145 5.2.3', NULL, NULL, N'3291', N'Les grands emballages de secours devront arborer la marque « SALVAGE » ou « SECOURS » près des marques décrites en 5.1.1. Les lettres du mot « SALVAGE » ou « SECOURS » doivent mesurer au moins 12 mm de haut.', N'Salvage large packagings shall additionally be marked with the word “SALVAGE” or “SECOURS” near the marks specified in 5.1.1. The lettering of the “SALVAGE” or “SECOURS” mark shall be at least 12 mm high.', N'Les grands emballages de secours devront arborer la marque « SALVAGE » ou « SECOURS » près des marques décrites en 5.1.1. Les lettres du mot « SALVAGE » ou « SECOURS » doivent mesurer au moins 12 mm de haut.', NULL)
