INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'15D7945B-ED6F-4554-BE04-762660091CA9', NULL, NULL, N'CGSB-43.145 5.2.4', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Large packagings manufactured from recycled plastic shall be marked “REC” near the primary marking.', N'Clause', N'CGSB-43.145 5.2.4', NULL, NULL, N'3292', N'Les grands emballages fabriqués avec du plastique recyclé doivent porter la marque « REC » près des marques de base.', N'Large packagings manufactured from recycled plastic shall be marked “REC” near the primary marking.', N'Les grands emballages fabriqués avec du plastique recyclé doivent porter la marque « REC » près des marques de base.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'1E66BA4E-CBC5-4276-A6FD-A6C0064A6649', NULL, NULL, N'CGSB-43.145 5.2.5.1', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'When the compliance mark on a large packaging is illegible or missing, the owner or the manufacturer shall replace the compliance mark in accordance with 5.2.5.2.', N'Clause', N'CGSB-43.145 5.2.5.1', NULL, NULL, N'3293', N'Lorsque l’indication de conformité figurant sur un grand emballage est illisible ou manquante, son propriétaire ou fabricant doit la remplacer, conformément à 5.2.5.2.', N'When the compliance mark on a large packaging is illegible or missing, the owner or the manufacturer shall replace the compliance mark in accordance with 5.2.5.2.', N'Lorsque l’indication de conformité figurant sur un grand emballage est illisible ou manquante, son propriétaire ou fabricant doit la remplacer, conformément à 5.2.5.2.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'15A3CD9A-0579-49B9-9DC9-151499CA1841', NULL, NULL, N'CGSB-43.145 5.2.5.2 a)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Prior to the installation of a replacement mark the owner shall have documented all the information on the compliance mark and can positively link the information to a specific large packaging.', N'Clause', N'CGSB-43.145 5.2.5.2 a)', NULL, NULL, N'3294', N'Avant d’apposer une indication de conformité de remplacement le propriétaire doit avoir documenté tous les renseignements pertinents sur l’indication de conformité et pouvoir positivement y associer un grand emballage particulier.', N'Prior to the installation of a replacement mark the owner shall have documented all the information on the compliance mark and can positively link the information to a specific large packaging.', N'Avant d’apposer une indication de conformité de remplacement le propriétaire doit avoir documenté tous les renseignements pertinents sur l’indication de conformité et pouvoir positivement y associer un grand emballage particulier.', NULL)
