INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'10D3CB85-2B3A-4197-9F92-B6639BE52446', NULL, NULL, N'CGSB-43.145 5.2.5.2 b)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Prior to the installation of a replacement mark the large packaging manufacturer can unequivocally link the large packaging to a set of data the manufacturer has kept on large packagings manufactured under its control.', N'Clause', N'CGSB-43.145 5.2.5.2 b)', NULL, NULL, N'3295', N'Avant d’apposer une indication de conformité de remplacement le fabricant du grand emballage peut associer sans équivoque le grand emballage à un ensemble de données que le fabricant a conservé sur les grands emballages fabriqués sous sa responsabilité.', N'Prior to the installation of a replacement mark the large packaging manufacturer can unequivocally link the large packaging to a set of data the manufacturer has kept on large packagings manufactured under its control.', N'Avant d’apposer une indication de conformité de remplacement le fabricant du grand emballage peut associer sans équivoque le grand emballage à un ensemble de données que le fabricant a conservé sur les grands emballages fabriqués sous sa responsabilité.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'335E34FF-548F-423E-879B-EB77F88B35B2', NULL, NULL, N'CGSB-43.145 6.1.1', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The large packaging shall be constructed of suitable ductile metal. Welds shall maintain their integrity when the large packaging is subjected to the test procedures required by this standard. Low temperature performance shall be taken into account when applicable.', N'Clause', N'CGSB-43.145 6.1.1', NULL, NULL, N'3296', N'Le grand emballage doit être fabriqué d’un métal ductile adéquat. Les soudures doivent conserver leur intégrité lorsque le grand emballage est soumis aux essais exigés en vertu de la présente norme. Le comportement à basse température doit être pris en considération s’il y a lieu.', N'The large packaging shall be constructed of suitable ductile metal. Welds shall maintain their integrity when the large packaging is subjected to the test procedures required by this standard. Low temperature performance shall be taken into account when applicable.', N'Le grand emballage doit être fabriqué d’un métal ductile adéquat. Les soudures doivent conserver leur intégrité lorsque le grand emballage est soumis aux essais exigés en vertu de la présente norme. Le comportement à basse température doit être pris en considération s’il y a lieu.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'72C9A742-22DE-4709-BEFA-E37FA88F4E56', NULL, NULL, N'CGSB-43.145 6.1.2', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Care shall be taken to avoid damage by galvanic action due to the juxtaposition of dissimilar metals.', N'Clause', N'CGSB-43.145 6.1.2', NULL, NULL, N'3297', N'Des soins particuliers doivent être pris afin d’éviter les dommages par action galvanique causée par la juxtaposition de métaux dissemblables.', N'Care shall be taken to avoid damage by galvanic action due to the juxtaposition of dissimilar metals.', N'Des soins particuliers doivent être pris afin d’éviter les dommages par action galvanique causée par la juxtaposition de métaux dissemblables.', NULL)
GO
