INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'48DAD785-287E-41E2-8539-933DFC433067', NULL, NULL, N'CGSB-43.145 6.2.2', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The seams of the large packaging shall be formed by stitching, heat sealing, gluing or an equally effective method. The ends of a stitched seam shall be secured.', N'Clause', N'CGSB-43.145 6.2.2', NULL, NULL, N'3300', N'Les joints du grand emballage doivent être réalisés par agrafage, scellage à chaud, collage ou selon une méthode d’efficacité équivalente. Les extrémités des joints agrafés doivent être scellées.', N'The seams of the large packaging shall be formed by stitching, heat sealing, gluing or an equally effective method. The ends of a stitched seam shall be secured.', N'Les joints du grand emballage doivent être réalisés par agrafage, scellage à chaud, collage ou selon une méthode d’efficacité équivalente. Les extrémités des joints agrafés doivent être scellées.', NULL)
