INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'85C8E193-5EFF-463D-9FBE-810F59C0449E', NULL, NULL, N'TDGR  2.43.1 (1)', N'Transportation of Dangerous Goods Regulations', N'TDGR  2.43.1::TMDR  2.43.1', N'A person must not handle, offer for transport or transport lithium cells and batteries under any of the following shipping names unless the cells and batteries meet the conditions set out in subsection (2):', N'Subsection', N' 2.43.1 (1)', N'TDGA 5 (a)', NULL, N'147', N'Il est interdit de transporter, de présenter au transport et de manutentionner les piles et batteries au lithium sous l’une ou l’autre des appellations réglementaires ci-après à moins qu’elles ne remplissent les conditions prévues au paragraphe (2) :', N'Handling, offering for transport or transporting lithium cells and batteries without meeting the conditions set out in subsection 2.43.1(2).', N'Manutention, demande de transport ou transport des piles ou batteries au lithium sans remplir les conditions énoncées au paragraphe 2.43.1 (2).', N'UN3090, LITHIUM METAL BATTERIES;::FR_UN3090, LITHIUM METAL BATTERIES;')
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'FC02B026-BECD-4B30-B3BE-8DF06F1F406D', NULL, NULL, N'CGSB-43.145 6.2.3 b)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The materials of construction shall provide resistance to aging and degradation caused by the external environment, to substance contained and, if required, by ultraviolet radiation. Additives may be included in the composition of the material to improve the resistance to aging or to serve other purposes, provided they do not adversely affect the physical or chemical properties of the material.', N'Clause', N'CGSB-43.145 6.2.3 b)', NULL, NULL, N'3302', N'Les matériaux de construction doivent offrir une résistance au vieillissement et à la dégradation causés par l’environnement extérieur, par une substance contenue et, le cas échéant, par les rayons ultraviolets. Des additifs peuvent être mélangés à la composition du matériau pour améliorer sa résistance au vieillissement ou pour toute autre raison, en autant qu’ils n’influencent pas négativement les propriétés physiques ou chimiques du matériau.', N'The materials of construction shall provide resistance to aging and degradation caused by the external environment, to substance contained and, if required, by ultraviolet radiation. Additives may be included in the composition of the material to improve the resistance to aging or to serve other purposes, provided they do not adversely affect the physical or chemical properties of the material.', N'Les matériaux de construction doivent offrir une résistance au vieillissement et à la dégradation causés par l’environnement extérieur, par une substance contenue et, le cas échéant, par les rayons ultraviolets. Des additifs peuvent être mélangés à la composition du matériau pour améliorer sa résistance au vieillissement ou pour toute autre raison, en autant qu’ils n’influencent pas négativement les propriétés physiques ou chimiques du matériau.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'1D351783-36D0-41AA-8243-BFDD77A1C433', NULL, NULL, N'CGSB-43.145 6.2.4', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Except for recycled plastic, no used material other than production residues or regrind from the same manufacturing process shall be used in the body.', N'Clause', N'CGSB-43.145 6.2.4', NULL, NULL, N'3303', N'À l’exception du plastique recyclé, aucun matériau déjà utilisé, autre que les restes ou chute de production provenant du même procédé de fabrication, ne peut être employé pour le corps.', N'Except for recycled plastic, no used material other than production residues or regrind from the same manufacturing process shall be used in the body.', N'À l’exception du plastique recyclé, aucun matériau déjà utilisé, autre que les restes ou chute de production provenant du même procédé de fabrication, ne peut être employé pour le corps.', NULL)
