INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'609E7261-AC44-4CC1-B896-43D372C747FE', NULL, NULL, N'CGSB-43.145 7.2.1.2', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'When a substitute is used to fill the inner packagings or other articles are used, they shall have the same physical characteristics (mass, etc.) as the filled inner packagings or articles to be transported. It is permissible to use materials, such as bags of lead shot, to achieve the requisite maximum permissible gross mass provided the materials are placed so that the test results are not affected.', N'Clause', N'CGSB-43.145 7.2.1.2', NULL, NULL, N'3333', N'Lorsqu’un substitut est utilisé pour remplir l’emballage intérieur ou d’autres objets sont utilisés, ceux-ci doivent posséder les mêmes caractéristiques physiques (masse, etc.) que les emballages intérieurs remplis ou objets qui seront transportés. Il est permis d’utiliser des matériaux comme des sacs de billes de plomb pour obtenir la masse brute maximale admissible, à condition que ces matériaux soient placés de manière à ne pas fausser les résultats d’essai.', N'When a substitute is used to fill the inner packagings or other articles are used, they shall have the same physical characteristics (mass, etc.) as the filled inner packagings or articles to be transported. It is permissible to use materials, such as bags of lead shot, to achieve the requisite maximum permissible gross mass provided the materials are placed so that the test results are not affected.', N'Lorsqu’un substitut est utilisé pour remplir l’emballage intérieur ou d’autres objets sont utilisés, ceux-ci doivent posséder les mêmes caractéristiques physiques (masse, etc.) que les emballages intérieurs remplis ou objets qui seront transportés. Il est permis d’utiliser des matériaux comme des sacs de billes de plomb pour obtenir la masse brute maximale admissible, à condition que ces matériaux soient placés de manière à ne pas fausser les résultats d’essai.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'759B7D81-CEA2-436A-ADC0-13B10DD72FFC', NULL, NULL, N'CGSB-43.145 7.2.1.3', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'For large packagings where the inner packagings are designed to contain liquid or solid dangerous goods, separate testing is required for both liquid and solid content.', N'Clause', N'CGSB-43.145 7.2.1.3', NULL, NULL, N'3334', N'Pour les grands emballages dans lesquels les emballages intérieurs sont destinés à transporter des marchandises dangereuses solides ou liquides, des essais distincts doivent avoir lieu pour les matières solides et liquides.', N'For large packagings where the inner packagings are designed to contain liquid or solid dangerous goods, separate testing is required for both liquid and solid content.', N'Pour les grands emballages dans lesquels les emballages intérieurs sont destinés à transporter des marchandises dangereuses solides ou liquides, des essais distincts doivent avoir lieu pour les matières solides et liquides.', NULL)
GO
