INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'EA9F4624-ABAD-46A2-876D-0E1F1BC0A484', NULL, NULL, N'CGSB-43.145 7.2.2.1.1 c)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Condition fibreboard large packagings in accordance with ASTM D685 or TAPPI T402. Condition for at least 24 h in an atmosphere having a controlled temperature and relative humidity (RH) with an average value in the range of one of the following: 27 ± 2°C and 65% ± 5% RH.', N'Clause', N'CGSB-43.145 7.2.2.1.1 c)', NULL, NULL, N'3338', N'Les grands emballages en carton doivent être conditionnés conformément à ASTM D685 ou à TAPPI T402. Le carton doit être conditionné pendant au moins 24 h, dans un milieu à température contrôlée avec une humidité relative (HR) d’une valeur moyenne correspondant à l’une des possibilités suivantes : 27 ± 2 °C et 65 % ± 5 % HR.', N'Condition fibreboard large packagings in accordance with ASTM D685 or TAPPI T402. Condition for at least 24 h in an atmosphere having a controlled temperature and relative humidity (RH) with an average value in the range of one of the following: 27 ± 2°C and 65% ± 5% RH.', N'Les grands emballages en carton doivent être conditionnés conformément à ASTM D685 ou à TAPPI T402. Le carton doit être conditionné pendant au moins 24 h, dans un milieu à température contrôlée avec une humidité relative (HR) d’une valeur moyenne correspondant à l’une des possibilités suivantes : 27 ± 2 °C et 65 % ± 5 % HR.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'46E64215-9008-470E-974A-41F855DCB0AE', NULL, NULL, N'CGSB-43.145 7.2.2.2', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Large packagings made of plastic materials and large packagings containing inner packagings of plastic materials, other than bags intended to contain solids or articles, shall be conditioned at a maximum temperature of -18°C in accordance with ASTM D7790.', N'Clause', N'CGSB-43.145 7.2.2.2', NULL, NULL, N'3339', N'Les grands emballages fabriqués de plastique et les grands emballages contenant des emballages intérieurs fabriqués de plastique, autre que des sacs devant contenir des solides ou des objets, doivent être conditionnés à une température ne dépassant pas -18 °C, conformément à ASTM D7790.', N'Large packagings made of plastic materials and large packagings containing inner packagings of plastic materials, other than bags intended to contain solids or articles, shall be conditioned at a maximum temperature of -18°C in accordance with ASTM D7790.', N'Les grands emballages fabriqués de plastique et les grands emballages contenant des emballages intérieurs fabriqués de plastique, autre que des sacs devant contenir des solides ou des objets, doivent être conditionnés à une température ne dépassant pas -18 °C, conformément à ASTM D7790.', NULL)
GO
