INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'B480B023-16BC-4C03-BA70-AAB91A84E8F4', NULL, NULL, N'CGSB-43.145 7.3.2', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Before testing in accordance with 7.3.3, the large packaging shall be filled and a load shall be added and evenly distributed. The mass of the filled large packaging and the load shall be 1.25 times the maximum permissible gross mass.', N'Clause', N'CGSB-43.145 7.3.2', NULL, NULL, N'3341', N'Avant de procéder à l’essai conformément à 7.3.3, remplir le grand emballage et ajouter une charge et la répartir également. La masse du grand emballage rempli et de la charge doit correspondre à 1,25 fois la masse brute maximale admissible du grand emballage.', N'Before testing in accordance with 7.3.3, the large packaging shall be filled and a load shall be added and evenly distributed. The mass of the filled large packaging and the load shall be 1.25 times the maximum permissible gross mass.', N'Avant de procéder à l’essai conformément à 7.3.3, remplir le grand emballage et ajouter une charge et la répartir également. La masse du grand emballage rempli et de la charge doit correspondre à 1,25 fois la masse brute maximale admissible du grand emballage.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'209A2478-3722-4006-AD0D-88A4794779F5', NULL, NULL, N'CGSB-43.145 7.3.3', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Raise and lower the large packaging twice by a lift truck. Space the forks at a distance equal to three quarters of the dimension of the side of the large packaging facing the lift truck and centrally position them, unless the points of entry are fixed. Penetrate the forks to three quarters of the dimension of the side of the large packaging facing the lift truck. Repeat the test from each possible direction of entry.', N'Clause', N'CGSB-43.145 7.3.3', NULL, NULL, N'3342', N'Lever et abaisser le grand emballage deux fois à l’aide d’un chariot élévateur. Espacer les bras du chariot élévateur à fourche de façon à obtenir une distance équivalant aux trois quarts de la dimension du côté du grand emballage faisant face au chariot et centrer les bras, sauf si les points d’insertion sont fixes. Enfoncer les bras jusqu’aux trois quarts de la dimension du côté du grand emballage faisant face au chariot élévateur. Répéter cet essai pour chaque direction d’insertion possible.', N'Raise and lower the large packaging twice by a lift truck. Space the forks at a distance equal to three quarters of the dimension of the side of the large packaging facing the lift truck and centrally position them, unless the points of entry are fixed. Penetrate the forks to three quarters of the dimension of the side of the large packaging facing the lift truck. Repeat the test from each possible direction of entry.', N'Lever et abaisser le grand emballage deux fois à l’aide d’un chariot élévateur. Espacer les bras du chariot élévateur à fourche de façon à obtenir une distance équivalant aux trois quarts de la dimension du côté du grand emballage faisant face au chariot et centrer les bras, sauf si les points d’insertion sont fixes. Enfoncer les bras jusqu’aux trois quarts de la dimension du côté du grand emballage faisant face au chariot élévateur. Répéter cet essai pour chaque direction d’insertion possible.', NULL)
GO
