INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'3CF3BFBC-9EA6-47BA-9FB7-6B3CC1F1DD7B', NULL, NULL, N'CGSB-43.145 7.3.4', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'After testing, there shall be no observable permanent deformation which renders the large packaging unsafe for transport and no release of the contents.', N'Clause', N'CGSB-43.145 7.3.4', NULL, NULL, N'3343', N'Après l’essai, il ne doit y avoir aucune déformation permanente observable qui pourrait rendre le grand emballage impropre au transport et il ne doit y avoir aucune perte de contenu.', N'After testing, there shall be no observable permanent deformation which renders the large packaging unsafe for transport and no release of the contents.', N'Après l’essai, il ne doit y avoir aucune déformation permanente observable qui pourrait rendre le grand emballage impropre au transport et il ne doit y avoir aucune perte de contenu.', NULL)
