INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'0D09BC56-7E2D-46C0-9E93-E3C0EEB983BA', NULL, NULL, N'CGSB-43.145 7.4.2 b)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Before testing in accordance with 7.4.3, follow the steps below, as applicable. Flexible large packagings shall be filled. A load shall be added and evenly distributed. The mass of the filled large packaging and the load shall be six times the maximum permissible gross mass.', N'Clause', N'CGSB-43.145 7.4.2 b)', NULL, NULL, N'3346', N'Avant de procéder à l’essai conformément à 7.4.3, il faut suivre les étapes suivantes, selon le cas. Les grands emballages souples doivent être remplis. Ajouter une charge et la répartir également. La masse du grand emballage rempli et de la charge doit correspondre à six fois la masse brute maximale admissible du grand emballage.', N'Before testing in accordance with 7.4.3, follow the steps below, as applicable. Flexible large packagings shall be filled. A load shall be added and evenly distributed. The mass of the filled large packaging and the load shall be six times the maximum permissible gross mass.', N'Avant de procéder à l’essai conformément à 7.4.3, il faut suivre les étapes suivantes, selon le cas. Les grands emballages souples doivent être remplis. Ajouter une charge et la répartir également. La masse du grand emballage rempli et de la charge doit correspondre à six fois la masse brute maximale admissible du grand emballage.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'EA005457-E2DA-4808-B182-007FF9BF8A6D', NULL, NULL, N'CGSB-43.145 7.4.3', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Lift the large packaging in accordance with its designed lifting procedure until it clears the floor and hold for at least 5 min.', N'Clause', N'CGSB-43.145 7.4.3', NULL, NULL, N'3347', N'Lever le grand emballage de la manière prévue à la conception jusqu’à ce qu’il ne touche plus le sol et le maintenir dans cette position pendant au moins 5 min.', N'Lift the large packaging in accordance with its designed lifting procedure until it clears the floor and hold for at least 5 min.', N'Lever le grand emballage de la manière prévue à la conception jusqu’à ce qu’il ne touche plus le sol et le maintenir dans cette position pendant au moins 5 min.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'0E6D3C20-C010-4E15-A328-3B8921887E99', NULL, NULL, N'CGSB-43.145 7.4.4 a)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Criteria for passing the test. Flexible large packaging: There shall be no observable permanent damage to the large packaging or its lifting devices which renders the large packaging unsafe for transport or handling and no release of the contents.', N'Clause', N'CGSB-43.145 7.4.4 a)', NULL, NULL, N'3348', N'Critères d’un essai réussi. Grand emballage souple : il ne doit y avoir aucun dommage au grand emballage ou aux dispositifs de levage qui rendrait le grand emballage impropre au transport ou à la manutention et il ne doit y avoir aucune perte de contenu.', N'Criteria for passing the test. Flexible large packaging: There shall be no observable permanent damage to the large packaging or its lifting devices which renders the large packaging unsafe for transport or handling and no release of the contents.', N'Critères d’un essai réussi. Grand emballage souple : il ne doit y avoir aucun dommage au grand emballage ou aux dispositifs de levage qui rendrait le grand emballage impropre au transport ou à la manutention et il ne doit y avoir aucune perte de contenu.', NULL)
