INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'0287A179-7F61-47D3-9FCD-54334583F92A', NULL, NULL, N'CGSB-43.145 7.5.3 b) 1)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Calculation of superimposed test load: the load to be placed on the large packaging shall be 1.8 times the combined maximum permissible gross mass of the number of large packagings of a similar large packaging design type that are intended to be stacked on top of the large packaging during transport.', N'Clause', N'CGSB-43.145 7.5.3 b) 1)', NULL, NULL, N'3354', N'Calcul de la charge d’essai superposée : la charge qui sera placée sur le grand emballage doit correspondre à 1,8 fois la masse brute maximale admissible combinée du nombre de grand emballage du même modèle type destiné à être empilé pardessus le grand emballage pendant le transport.', N'Calculation of superimposed test load: the load to be placed on the large packaging shall be 1.8 times the combined maximum permissible gross mass of the number of large packagings of a similar large packaging design type that are intended to be stacked on top of the large packaging during transport.', N'Calcul de la charge d’essai superposée : la charge qui sera placée sur le grand emballage doit correspondre à 1,8 fois la masse brute maximale admissible combinée du nombre de grand emballage du même modèle type destiné à être empilé pardessus le grand emballage pendant le transport.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'E8054605-55B8-4925-A6EC-8F991B451921', NULL, NULL, N'CGSB-43.145 7.5.3 c) 1)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Subject the large packaging to the test load for the applicable time and conditions below: all types of large packagings other than metal large packagings — 24 h.', N'Clause', N'CGSB-43.145 7.5.3 c) 1)', NULL, NULL, N'3355', N'Appliquer une charge d’essai sur le grand emballage pendant la durée applicable et les conditions ci-dessous : tous les grands emballages, à l’exception des grands emballages en métal — 24 h.', N'Subject the large packaging to the test load for the applicable time and conditions below: all types of large packagings other than metal large packagings — 24 h.', N'Appliquer une charge d’essai sur le grand emballage pendant la durée applicable et les conditions ci-dessous : tous les grands emballages, à l’exception des grands emballages en métal — 24 h.', NULL)
