INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'831AA9B3-EE47-4F16-8379-28045481D61E', NULL, NULL, N'CGSB-43.145 7.7.3', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The fibreboard shall not absorb more than 155 g/m2 of water.', N'Clause', N'CGSB-43.145 7.7.3', NULL, NULL, N'3370', N'Le carton ne doit pas absorber plus de 155 g/m2 d’eau.', N'The fibreboard shall not absorb more than 155 g/m2 of water.', N'Le carton ne doit pas absorber plus de 155 g/m2 d’eau.', NULL)
