INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'FE932779-9E59-43BB-9353-3EF27B4A9E22', NULL, NULL, N'CGSB-43.145 7.8.2', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The pressure shall be measured by use of a gauge of suitable range and accuracy. The gauge shall be regularly calibrated or be frequently verified for accuracy against a regularly calibrated gauge.', N'Clause', N'CGSB-43.145 7.8.2', NULL, NULL, N'3372', N'La pression doit être mesurée au moyen d’un manomètre ayant une étendue de mesure et une précision appropriées. Le manomètre doit être régulièrement étalonné ou vérifié fréquemment pour assurer sa précision en le comparant à un manomètre régulièrement étalonné.', N'The pressure shall be measured by use of a gauge of suitable range and accuracy. The gauge shall be regularly calibrated or be frequently verified for accuracy against a regularly calibrated gauge.', N'La pression doit être mesurée au moyen d’un manomètre ayant une étendue de mesure et une précision appropriées. Le manomètre doit être régulièrement étalonné ou vérifié fréquemment pour assurer sa précision en le comparant à un manomètre régulièrement étalonné.', NULL)
