INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'F6AB19D4-CAA7-41AA-9FCC-DA8943646EBD', NULL, NULL, N'CGSB-43.145 7.8.4', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'There shall be no leakage of air from the large packaging.', N'Clause', N'CGSB-43.145 7.8.4', NULL, NULL, N'3375', N'Il ne doit y avoir aucune fuite d’air du grand emballage.', N'There shall be no leakage of air from the large packaging.', N'Il ne doit y avoir aucune fuite d’air du grand emballage.', NULL)
