INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'48FA482D-06BC-4436-B868-A4CF948497DF', NULL, NULL, N'CGSB-43.145 9.2', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The quality management system shall conform to the requirements of ISO 9001 and be registered with a quality management system registrar (registration organization) accredited by the Standards Council of Canada (SCC), or a foreign quality management system registrar recognized by the SCC.', N'Clause', N'CGSB-43.145 9.2', NULL, NULL, N'3377', N'Le système de management de la qualité doit être conforme aux exigences de la norme ISO 9001 et enregistré auprès d’un registraire de systèmes de management de la qualité accrédité ou reconnu par le Conseil canadien des normes (CCN) ou auprès d’un registraire étranger de systèmes d’assurance qualité reconnu par le CCN.', N'The quality management system shall conform to the requirements of ISO 9001 and be registered with a quality management system registrar (registration organization) accredited by the Standards Council of Canada (SCC), or a foreign quality management system registrar recognized by the SCC.', N'Le système de management de la qualité doit être conforme aux exigences de la norme ISO 9001 et enregistré auprès d’un registraire de systèmes de management de la qualité accrédité ou reconnu par le Conseil canadien des normes (CCN) ou auprès d’un registraire étranger de systèmes d’assurance qualité reconnu par le CCN.', NULL)
