INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'F0C015BC-B67F-4E1D-98CF-FB6F671E211C', NULL, NULL, N'CGSB-43.145 11.1 d)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Except as permitted in clauses 12, 13 and 14, a large packaging shall not be used to handle, offer for transport, and transport dangerous goods unless: where large packaging codes are listed in the packing instructions, the large packaging is a UN standardized
large packaging of that packaging code and is marked accordingly.', N'Clause', N'CGSB-43.145 11.1 d)', NULL, NULL, N'3397', N'Sauf dans la mesure prévue aux sections 12, 13 et 14, il est interdit d’utiliser un grand emballage pour se livrer à la manutention, à la présentation au transport ou au transport de matières dangereuses, à moins que : lorsque des codes de grands emballages sont énumérés dans les instructions d’emballages, le grand emballage ne soit un grand emballage normalisé UN et ne soit marqué en conséquence.', N'Except as permitted in clauses 12, 13 and 14, a large packaging shall not be used to handle, offer for transport, and transport dangerous goods unless: where large packaging codes are listed in the packing instructions, the large packaging is a UN standardized
large packaging of that packaging code and is marked accordingly.', N'Sauf dans la mesure prévue aux sections 12, 13 et 14, il est interdit d’utiliser un grand emballage pour se livrer à la manutention, à la présentation au transport ou au transport de matières dangereuses, à moins que : lorsque des codes de grands emballages sont énumérés dans les instructions d’emballages, le grand emballage ne soit un grand emballage normalisé UN et ne soit marqué en conséquence.', NULL)
