INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'EE7F927C-D758-4BAF-8FBD-292214C4BDB7', NULL, NULL, N'CGSB-43.145 11.2', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'A large packaging that is used more than once, shall be in such condition, including closure devices and cushioning materials, that they conform to all applicable requirements of the standard.', N'Clause', N'CGSB-43.145 11.2', NULL, NULL, N'3401', N'Un grand emballage qui est utilisé plus d’une fois doit être dans une telle condition, y compris ses dispositifs de fermeture et matériaux de rembourrage, qu’il se conforme à toutes les exigences applicables en vigueur dans la présente norme.', N'A large packaging that is used more than once, shall be in such condition, including closure devices and cushioning materials, that they conform to all applicable requirements of the standard.', N'Un grand emballage qui est utilisé plus d’une fois doit être dans une telle condition, y compris ses dispositifs de fermeture et matériaux de rembourrage, qu’il se conforme à toutes les exigences applicables en vigueur dans la présente norme.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'CF33F747-AA8A-4BD4-9430-82087541AC04', NULL, NULL, N'CGSB-43.145 11.3', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'A large packaging shall not be filled with dangerous goods unless the large packaging is free of any visible defect that shows signs of reduced strength compared with the registered design type that may render the large packaging unsafe for transport.', N'Clause', N'CGSB-43.145 11.3', NULL, NULL, N'3402', N'Un grand emballage ne doit être rempli de marchandises dangereuses à moins d’être exempt de tout défaut visible pouvant montrer des signes d’affaiblissement comparativement au modèle inscrit et rendre celui-ci impropre pour le transport.', N'A large packaging shall not be filled with dangerous goods unless the large packaging is free of any visible defect that shows signs of reduced strength compared with the registered design type that may render the large packaging unsafe for transport.', N'Un grand emballage ne doit être rempli de marchandises dangereuses à moins d’être exempt de tout défaut visible pouvant montrer des signes d’affaiblissement comparativement au modèle inscrit et rendre celui-ci impropre pour le transport.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'614C81D8-A0DA-4C7C-9691-A99552BCBA4C', NULL, NULL, N'CGSB-43.145 11.4 a)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The following requirements shall apply: the maximum permissible gross mass specified in the marking of a large packaging shall not be exceeded.', N'Clause', N'CGSB-43.145 11.4 a)', NULL, NULL, N'3403', N'Les exigences suivantes s’appliquent : la masse brute maximale admissible définie par le marquage du grand emballage ne doit pas être dépassée.', N'The following requirements shall apply: the maximum permissible gross mass specified in the marking of a large packaging shall not be exceeded.', N'Les exigences suivantes s’appliquent : la masse brute maximale admissible définie par le marquage du grand emballage ne doit pas être dépassée.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'C4C01D3A-9F43-445A-8EC6-765EAC193DAD', NULL, NULL, N'CGSB-43.145 11.4 b)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The following requirements shall apply: the large packaging shall be closed as instructed in the information provided by the large packaging manufacturer or distributor in accordance with 4.7.', N'Clause', N'CGSB-43.145 11.4 b)', NULL, NULL, N'3404', N'Les exigences suivantes s’appliquent : le grand emballage doit être fermé conformément aux instructions fournies par le fabricant ou le distributeur du grand emballage, conformément à 4.7.', N'The following requirements shall apply: the large packaging shall be closed as instructed in the information provided by the large packaging manufacturer or distributor in accordance with 4.7.', N'Les exigences suivantes s’appliquent : le grand emballage doit être fermé conformément aux instructions fournies par le fabricant ou le distributeur du grand emballage, conformément à 4.7.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'71B64525-0A33-4268-9627-96EDCE7A6EFD', NULL, NULL, N'CGSB-43.145 11.4 c)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The following requirements shall apply: where pressure may develop in a large packaging by the evolution of gas from the contents, the large packaging shall be equipped with a vent provided that any gas emitted will not cause danger to public safety. The vent shall be designed so that when the large packaging is in the position in which it is intended to be transported, leakage of liquid and penetration of foreign substances are prevented under normal conditions of transport.', N'Clause', N'CGSB-43.145 11.4 c)', NULL, NULL, N'3405', N'Les exigences suivantes s’appliquent : si une pression risque de s’accumuler dans un grand emballage en raison du gaz dégagé par le contenu, le grand emballage doit être muni d’un évent, à condition que le gaz émis n’entraîne aucun danger pour la sécurité publique. L’évent doit être conçu de manière à empêcher toute fuite de liquide et la pénétration de substances étrangères au cours de transports effectués dans des conditions normales, le grand emballage étant placé dans la position prévue pour le transport.', N'The following requirements shall apply: where pressure may develop in a large packaging by the evolution of gas from the contents, the large packaging shall be equipped with a vent provided that any gas emitted will not cause danger to public safety. The vent shall be designed so that when the large packaging is in the position in which it is intended to be transported, leakage of liquid and penetration of foreign substances are prevented under normal conditions of transport.', N'Les exigences suivantes s’appliquent : si une pression risque de s’accumuler dans un grand emballage en raison du gaz dégagé par le contenu, le grand emballage doit être muni d’un évent, à condition que le gaz émis n’entraîne aucun danger pour la sécurité publique. L’évent doit être conçu de manière à empêcher toute fuite de liquide et la pénétration de substances étrangères au cours de transports effectués dans des conditions normales, le grand emballage étant placé dans la position prévue pour le transport.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'AEFE57B7-B92F-44E4-BEDF-C2D468D2D569', NULL, NULL, N'CGSB-43.145 11.4 d)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'The following requirements shall apply: combinations of different dangerous goods within the large packaging shall be in accordance with 13.1.', N'Clause', N'CGSB-43.145 11.4 d)', NULL, NULL, N'3406', N'Les exigences suivantes s’appliquent : la combinaison de différentes marchandises dangereuses à l’intérieur d’un même grand emballage doit être faite conformément à 13.1.', N'The following requirements shall apply: combinations of different dangerous goods within the large packaging shall be in accordance with 13.1.', N'Les exigences suivantes s’appliquent : la combinaison de différentes marchandises dangereuses à l’intérieur d’un même grand emballage doit être faite conformément à 13.1.', NULL)
