INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'FFF82801-1C0B-4AAC-9D93-B2D5277EB34B', NULL, NULL, N'TDGR 1.17.1 (2)', N'Transportation of Dangerous Goods Regulations', N'TDGR 1.17.1 (2)::TMDR 1.17.1 (2)', N'When dangerous goods in excepted quantities for which different alphanumeric codes are assigned are together in an outer means of containment, the total quantity of dangerous goods must not exceed the lowest maximum net quantity per outer means of containment that is set out in column 2 of the table to this subsection for any of the dangerous goods.', N'Subsection', N'1.17.1 (2)', N'TDGA 5 (a)', NULL, N'38', N'Lorsque les marchandises dangereuses qui sont en quantités exceptées et auxquelles sont attribuées des codes alphanumériques différents sont placées ensemble dans un contenant extérieur, la quantité totale de marchandises dangereuses ne peut dépasser la quantité maximale nette la moins élevée, pour l’une ou l’autre des marchandises dangereuses, par contenant extérieur, prévue à la colonne 2 du tableau du présent paragraphe.', N'Handle, offer for transport , transport or import dangerous goods in excepted quantities for which different alphanumeric codes are assigned and are together in an outer means of containment, in a quantity exceeding the limit of the lowest maximum net quantity per outer means of containment that is set out in column 2 of the table of subsection 1.17.1(2) for any of the dangerous goods.', N'Manutention, demande de transport ou transport ou importation de marchandises dangereuses en quantités exceptées pour lesquelles différents codes alphanumériques sont attribués et qui sont placées ensemble  dans un contenant extérieur en une quantité dépassant la limite de la quantité maximale nette la moins élevée, pour l’une ou l’autre des marchandises dangereuses, par contenant extérieur, prévue à la colonne 2  du tableau du paragraphe 1.17.1 (2) .', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'45342D59-2522-466B-8592-9F9A5347B6E7', NULL, NULL, N'TDGR 1.17.1 (4) (a)', N'Transportation of Dangerous Goods Regulations', N'TDGR 1.17.1 (4) (a)::TMDR  1.17.1 (4) (a)', N'the word “Overpack” or “Suremballage”; and', N'Paragraph', N'1.17.1 (4) (a)', N'TDGA 5 (d)', NULL, N'39', N'le mot « Suremballage » ou « Overpack »;', N'Handle, offer for transport, transport or import dangerous goods if the dangerous goods in excepted quantities that are in a means of containment that is inside an overpack without the word “Overpack” or “Suremballage” is displayed on the overpack, unless that information is on the means of containment and is visible through the overpack:            ', N'Manutention, demande de transport ou transport ou importation de marchandises dangereuses, si les marchandises dangereuses en quantités exceptées sont dans un contenant qui se trouve à l''intérieur d''un suremballage et que le mot «Suremballage» ou «Overpack» soit affiché sur le suremballage, à moins que cette information ne figure sur le contenant et est visible à travers le suremballage:', NULL)
GO
