INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'365EAA49-F8D9-47E0-9C0F-D4CFD5DD020C', NULL, NULL, N'TDGR  3.1 (2)', N'Transportation of Dangerous Goods Regulations', N'TDGR  3.1::TMDR  3.1', N'When dangerous goods are imported into Canada, the consignor must, before the dangerous goods are transported in Canada, ensure that the carrier has a shipping document or, with the agreement of the carrier, an electronic copy of the shipping document that contains the information required by these Regulations.', N'Subsection', N' 3.1 (2)', N'TDGA 5 (b)', NULL, N'149', N'Dans le cas de marchandises dangereuses importées au Canada, l’expéditeur doit s’assurer que, avant leur transport au Canada, le transporteur possède un document d’expédition ou, avec l’accord de celui-ci, une copie électronique du document d’expédition qui contient les renseignements exigés par le présent règlement.', N'Consignor failing to ensure that, before transport, carrier has shipping document for dangerous goods imported into Canada.', N'L''expéditeur omet de s''assurer que, avant le transport, le transporteur possède un document d''expédition pour les marchandises dangereuses importées au Canada.', N'When dangerous goods are imported into Canada, the consignor must, before the dangerous goods are transported in Canada, ensure that the carrier has a shipping document or, with the agreement of the carrier, an electronic copy of the shipping document that contains the information required by these Regulations.::FR_When dangerous goods are imported into Canada, the consignor must, before the dangerous goods are transported in Canada, ensure that the carrier has a shipping document or, with the agreement of the carrier, an electronic copy of the shipping document that contains the information required by these Regulations.')
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'1D475CF6-C104-4934-BA2C-0618177C98C4', NULL, NULL, N'TDGR  3.2 (1)', N'Transportation of Dangerous Goods Regulations', N'TDGR  3.2::TMDR  3.2', N'A carrier must not take possession of dangerous goods for transport unless the carrier has the shipping document for the dangerous goods.', N'Subsection', N' 3.2 (1)', N'TDGA 5 (b)', NULL, N'150', N'Il est interdit à tout transporteur de prendre possession de marchandises dangereuses en vue de leur transport à moins d’avoir le document d’expédition pour ces marchandises dangereuses.', N'Carrier taking possession of dangerous goods without having shipping document for the goods.', N'Le transporteur prend possession de marchandises dangereuses sans avoir le document d''expédition pour ces marchandises.', N'A carrier must not take possession of dangerous goods for transport unless the carrier has the shipping document for the dangerous goods.::FR_A carrier must not take possession of dangerous goods for transport unless the carrier has the shipping document for the dangerous goods.')
GO
