INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'1B7006EB-3E8D-43FC-989D-6CAB0D67995C', NULL, NULL, N'TDGR  3.9 (1) (b)', N'Transportation of Dangerous Goods Regulations', N'TDGR  3.9 (1)::TMDR  3.9 (1)', N'a list that includes the classification of the dangerous goods.', N'Paragraph', N' 3.9 (1) (b)', N'TDGA 5 (b)', NULL, N'199', N'soit d’une liste indiquant la classification des marchandises dangereuses.', N'Master of vessel containing dangerous goods or master in control of a vessel containing dangerous goods failing to have readily available copy or electronic copy of the shipping document or a list that includes the classification of the dangerous goods on or near the bridge of the ship.', N'Le capitaine du batiment qui contient des marchandises dangereuses ou le capitaine ayant la direction d’un tel batiment omet d’avoir une copie ou une version électronique du document d''expédition ou d’une liste indiquant la classification des marchandises dangereuses sur ou près de la passerelle.', N'a list that includes the classification of the dangerous goods.::FR_a list that includes the classification of the dangerous goods.')
