INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'C95B8E4E-0979-45BB-8688-1A7C603188F0', NULL, NULL, N'TDGR 5.7 (2)', N'Transportation of Dangerous Goods Regulations', N'TDGR  5.7::TMDR  5.7', N'For a mixed load of two or more explosives with compatibility groups C, D, E, N or S, the compatibility group of the mixed load is the first compatibility group of E, D, C, N or S present in the mixed load.', N'Subsection', N' 5.7 (1)', N'TDGA 5 (a)', NULL, N'321', N'Lorsqu’un chargement mixte comprend deux ou plusieurs explosifs appartenant aux groupes de compatibilité C, D, E, N ou S, le groupe de compatibilité du chargement mixte est le premier groupe entre E, D, C, N ou S auquel des explosifs appartiennent dans le chargement mixte.', N'Failure to display the correct compatibility group for a mixed load of explosives when the compatibility groups are C, D, E, N or S.', N'Omission d’apposer le bon groupe de compatibilité pour un chargement mixte d’explosifs des groupes de compatibilités C, D, E, N ou S.', NULL)
