INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'74494721-AC1A-4267-A7FE-3B6F4ED2A89D', NULL, NULL, N'TDGR  6.6', N'Transportation of Dangerous Goods Regulations', N'TDGR TDG Regulations::TMDR TDG Regulations', N'An employer or a self-employed person must keep a record of training or a statement of experience, as well as a copy of a training certificate, in electronic or paper form, beginning on the date the training certificate is issued and continuing until two years after the date it expires.', N'Section', N' 6.6', N'TDGA 5 (a)', NULL, N'378', N'Tout employeur ou tout travailleur autonome conserve, sous forme électronique ou sur papier, un dossier de formation ou un énoncé d’expérience, ainsi qu’une copie du certificat de formation, à compter de la date de sa délivrance jusqu’à deux ans après sa date d’expiration.', N'Employer or self-employed person failing to keep record of training and/or copy of training certificate.', N'L''employeur ou le travailleur autonome omet de conserver un dossier de formation ou une copie du certificat de formation.', N'An employer or a self-employed person must keep a record of training or a statement of experience, as well as a copy of a training certificate, in electronic or paper form, beginning on the date the training certificate is issued and continuing until two years after the date it expires.::FR_An employer or a self-employed person must keep a record of training or a statement of experience, as well as a copy of a training certificate, in electronic or paper form, beginning on the date the training certificate is issued and continuing until two years after the date it expires.')
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'A5ED24DD-2F90-4FEF-A14F-4F442F6F2A35', NULL, NULL, N'TDGR  6.7', N'Transportation of Dangerous Goods Regulations', N'TDGR TDG Regulations::TMDR TDG Regulations', N'Within 15 days after the date of a written request by an inspector, the employer of a person who holds a training certificate or a self-employed person must provide a copy of the training certificate to the inspector and, if applicable, a copy of the record of training or the statement of experience and a description of the training material used in the person’s training.', N'Section', N' 6.7', N'TDGA 5 (a)', NULL, N'379', N'L’employeur d’une personne qui est titulaire d’un certificat de formation ou le travailleur autonome présente à l’inspecteur, dans les quinze jours suivant la date d’une demande écrite de celui-ci, une copie du certificat de formation et, le cas échéant, du dossier de formation ou de l’énoncé d’expérience, et la description du matériel didactique utilisé pour la formation de la personne.', N'Employer or self-employed person failing to provide copy of training certificate and/or training record within 15 days after written request by Inspector.', N'L''employeur ou le travailleur autonome omet de présenter une copie du certificat de formation ou du dossier de formation dans les 15 jours suivant la date d''une demande écrite de l''inspecteur.', N'Within 15 days after the date of a written request by an inspector, the employer of a person who holds a training certificate or a self-employed person must provide a copy of the training certificate to the inspector and, if applicable, a copy of the record of training or the 