INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'68615166-DFD5-4CFC-9E62-F27F1F66CDC7', NULL, NULL, N'TDGR  12.9 (12) (b)', N'Transportation of Dangerous Goods Regulations', N'TDGR  12.9 (12)::TMDR  12.9 (12)', N'the quantity of the dangerous goods in an inner means of containment must be less than or equal to 5 L or 5 kg and, in an outer means of containment must be less than or equal to 20 L or 20 kg;', N'Paragraph', N' 12.9 (12) (b)', N'TDGA 5 (c)', NULL, N'630', N'la quantité des marchandises dangereuses dans un contenant intérieur est inférieure ou égale à 5 L ou 5 kg et, dans le cas d’un contenant extérieur, inférieure ou égale à 20 L ou 20 kg;', NULL, NULL, N'the quantity of the dangerous goods in an inner means of containment must be less than or equal to 5 L or 5 kg and, in an outer means of containment must be less than or equal to 20 L or 20 kg;::FR_the quantity of the dangerous goods in an inner means of containment must be less than or equal to 5 L or 5 kg and, in an outer means of containment must be less than or equal to 20 L or 20 kg;')
