INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'9981830F-CDEE-4BC5-B304-C301FCF4A617', NULL, NULL, N'TDGR  12.12 (3) (g)', N'Transportation of Dangerous Goods Regulations', N'TDGR  12.12 (3)::TMDR  12.12 (3)', N'the person who loads and secures or directly supervises the loading and securing of dangerous goods on board the aircraft', N'Paragraph', N' 12.12 (3) (g)', N'TDGA 5 (a)', NULL, N'665', N'à ce que la personne qui charge et arrime des marchandises dangereuses à bord de l’aéronef ou qui en surveille directement le chargement et l’arrimage, à la fois : (i) se conforme à l’article 3.1, Inspection pour déceler des dommages ou des déperditions, du chapitre 3, Inspection et décontamination, de la 7e Partie, Responsabilités de l’exploitant, des Instructions techniques de l’OACI,
(ii) sépare les contenants dans lesquels sont placées des marchandises dangereuses qui pourraient réagir dangereusement entre elles en cas de rejet, conformément au tableau 7-1, intitulé « Séparation entre colis », du chapitre 2, Entreposage et chargement, de la 7e Partie, Responsabilités de l’exploitant, des Instructions techniques de l’OACI.', NULL, NULL, N'the person who loads and secures or directly supervises the loading and securing of dangerous goods on board the aircraft::FR_the person who loads and secures or directly supervises the loading and securing of dangerous goods on board the aircraft')
GO
