INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'CA23E6F6-3C55-4872-9BC3-61D0DBF05AAD', NULL, NULL, N'CGSB-43.123 4.1.7.1', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'Containers may be seamless or with seams, except that a seam in the cylindrical portion of a TC-2R aerosol container is not authorized. Circumferential seams shall be welded, swedged, brazed, soldered or doubleseamed. Longitudinal seams shall be welded, brazed or soldered.', N'Clause', N'CGSB-43.123 4.1.7.1', N'TDGA 5 (c)', N'TDGR 5.11', N'3045', N'Les contenants peuvent tre fabriqus avec ou sans soudures. Par contre, il est interdit de souder la partie cylindrique dune bombe arosol TC-2R. Les joints circonfrentiels doivent tre raliss par soudage, estampage, brasage fort, brasage tendre ou double oudure. Les joints longitudinaux doivent tre raliss par soudage, brasage fort ou brasage tendre.', N'Containers may be seamless or with seams, except that a seam in the cylindrical portion of a TC-2R aerosol container is not authorized. Circumferential seams shall be welded, swedged, brazed, soldered or doubleseamed. Longitudinal seams shall be welded, brazed or soldered.', N'Les contenants peuvent tre fabriqus avec ou sans soudures. Par contre, il est interdit de souder la partie cylindrique dune bombe arosol TC-2R. Les joints circonfrentiels doivent tre raliss par soudage, estampage, brasage fort, brasage tendre ou double oudure. Les joints longitudinaux doivent tre raliss par soudage, brasage fort ou brasage tendre.', NULL)
