INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'3054F410-E50F-4093-BFCA-B2EDAC5F899C', NULL, NULL, N'CGSB-43.125 7.3.1.6', N'CGSB-43.125 - Packaging of Category A and Category B infectious substances (Class 6.2) and clinical, (bio) medical or regulated medical waste', NULL, N'The drop test shall be performed with the packagings in the conditioning atmosphere, specified in 7.3.2, or within 15 min of removal from the conditioning atmosphere.', N'Clause', N'CGSB-43.125 7.3.1.6', N'TDGA 5 (c)', NULL, N'3206', N'Lessai de chute doit tre ralis avec les emballages dans latmosphre de conditionnement, spcifie en 7.3.2, ou dans les 15 min suivant le retrait de latmosphre de conditionnement.', N'The drop test shall be performed with the packagings in the conditioning atmosphere, specified in 7.3.2, or within 15 min of removal from the conditioning atmosphere.', N'Lessai de chute doit tre ralis avec les emballages dans latmosphre de conditionnement, spcifie en 7.3.2, ou dans les 15 min suivant le retrait de latmosphre de conditionnement.', NULL)
