INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'5A8D9F83-817D-48FF-8DA0-4A095C168CCB', NULL, NULL, N'CGSB-43.123 8.1.3.4', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'The following records shall be retained for a minimum of ten years: Records of test results for the hot water bath test with a record of both the temperature and duration of the test.', N'Clause', N'CGSB-43.123 8.1.3.4', N'TDGA 5 (c)', N'TDGR 5.11', N'3119', N'Les registres suivants doivent tre conservs pendant au moins dix ans : les rsultats dessai dans le bain deau chaude avec la temprature et la dure de lessai.', N'The following records shall be retained for a minimum of ten years: Records of test results for the hot water bath test with a record of both the temperature and duration of the test.', N'Les registres suivants doivent tre conservs pendant au moins dix ans : les rsultats dessai dans le bain deau chaude avec la temprature et la dure de lessai.', NULL)
