INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'02D90D06-6F45-4F87-A7F3-EB9AB3E20DFE', NULL, NULL, N'CGSB-43.123 8.1.4.2', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'Prior to filling, each container shall be subjected to an internal pressure equal to or greater than the internal pressure in the containers at 55C when filled for transport with the gas. This pressure shall be at least two-thirds of the minimum burst pressure of the container. If any container shows evidence of distortion, leakage at a rate equal to or greater than 3.3 x 10-2 mbar L s-1 at 20C at the test pressure, or any other defect, it shall be condemned.', N'Clause', N'CGSB-43.123 8.1.4.2', N'TDGA 5 (c)', N'TDGR 5.11', N'3120', N'Avant le remplissage, chaque contenant doit tre soumis  une pression interne gale ou suprieure  la pression interne des contenants  55 C lorsquils sont remplis de gaz en vue de leur transport. Cette pression doit correspondre au moins aux deux tiers de la pression de rupture minimale du contenant. Tout contenant qui prsente une distorsion, une fuite  un taux gal ou suprieur  3,3 x 10-2 mbar Ls-1  20 C  la pression dessai, ou toute autre dfectuosit, doit tre refus.', N'Prior to filling, each container shall be subjected to an internal pressure equal to or greater than the internal pressure in the containers at 55C when filled for transport with the gas. This pressure shall be at least two-thirds of the minimum burst pressure of the container. If any container shows evidence of distortion, leakage at a rate equal to or greater than 3.3 x 10-2 mbar L s-1 at 20C at the test pressure, or any other defect, it shall be condemned.', N'Avant le remplissage, chaque contenant doit tre soumis  une pression interne gale ou suprieure  la pression interne des contenants  55 C lorsquils sont remplis de gaz en vue de leur transport. Cette pression doit correspondre au moins aux deux tiers de la pression de rupture minimale du contenant. Tout contenant qui prsente une distorsion, une fuite  un taux gal ou suprieur  3,3 x 10-2 mbar Ls-1  20 C  la pression dessai, ou toute autre dfectuosit, doit tre refus.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'E93D4245-BA12-49A9-9FBB-73A2F55A7860', NULL, NULL, N'CGSB-43.123 8.1.4.3', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'The temperature referred to in 8.1.4.2 may be reduced to 50C if the container is not more than 95% liquid full at 50C.', N'Clause', N'CGSB-43.123 8.1.4.3', N'TDGA 5 (c)', N'TDGR 5.11', N'3121', N'La temprature indique en 8.1.4.2 peut tre rduite  50 C, si au plus 95 % du contenant est rempli de liquide  50 C.', N'The temperature referred to in 8.1.4.2 may be reduced to 50C if the container is not more than 95% liquid full at 50C.', N'La temprature indique en 8.1.4.2 peut tre rduite  50 C, si au plus 95 % du contenant est rempli de liquide  50 C.', NULL)
